INSERT INTO `rex_module` VALUES ('4','07 - Download','0','<?php\r\n\r\n	if (!function_exists(\'Dateigroesse\')) {\r\n		function Dateigroesse($URL) {\r\n			$Groesse = @filesize($URL);\r\n			if($Groesse<1000) {\r\n				return number_format($Groesse, 0, \",\", \".\").\" Bytes\";\r\n			}\r\n			elseif($Groesse<1000000) {\r\n				return number_format($Groesse/1024, 0, \",\", \".\").\" kB\";\r\n			}\r\n			else {\r\n				return number_format($Groesse/1048576, 0, \",\", \".\").\" MB\";\r\n			}\r\n		}\r\n	}\r\n	\r\n	if (!function_exists(\'parse_icon\')) {\r\n		function parse_icon($ext) {\r\necho \"<!-- $ext -->\";\r\n			switch (strtolower($ext)) {\r\n	\r\n				case \'doc\': return \'mime-doc.gif\';\r\n				case \'rtf\': return \'mime-doc.gif\';\r\n				case \'txt\': return \'mime-txt.gif\';\r\n				case \'xls\': return \'mime-xls.gif\';\r\n				case \'eps\': return \'mime-eps.gif\';\r\n				case \'csv\': return \'mime-xls.gif\';\r\n				case \'ppt\': return \'mime-ppt.gif\';\r\n				case \'html\': return \'mime-html.gif\';\r\n				case \'htm\': return \'mime-html.gif\';\r\n				case \'php\': return \'mime-script.gif\';\r\n				case \'php3\': return \'mime-script.gif\';\r\n				case \'cgi\': return \'mime-script.gif\';\r\n				case \'pdf\': return \'mime-pdf.gif\';\r\n				case \'rar\': return \'mime-rar.gif\';\r\n				case \'zip\': return \'mime-zip.gif\';\r\n				case \'gz\': return \'mime-gz.gif\';\r\n				case \'jpg\': return \'mime-jpg.gif\';\r\n				case \'gif\': return \'mime-gif.gif\';\r\n				case \'png\': return \'mime-png.gif\';\r\n				case \'bmp\': return \'mime-image.gif\';\r\n				case \'tif\': return \'mime-image.gif\';\r\n				case \'exe\': return \'mime-binary.gif\';\r\n				case \'bin\': return \'mime-binary.gif\';\r\n				case \'avi\': return \'mime-mov.gif\';\r\n				case \'mpg\': return \'mime-mov.gif\';\r\n				case \'moc\': return \'mime-mov.gif\';\r\n				case \'asf\': return \'mime-mov.gif\';\r\n				case \'mp3\': return \'mime-sound.gif\';\r\n				case \'wav\': return \'mime-sound.gif\';\r\n				case \'org\': return \'mime-sound.gif\';\r\n			\r\n				default:\r\n					return \'icon_def.gif\';\r\n			}\r\n		}\r\n	}\r\n	$out	= \"\";\r\n	\r\n	$extFile1	= \"\";\r\n	$nameFile1	= \"\";\r\n	$sizeFile1	= \"\";\r\n	\r\n	if (\"REX_FILE[1]\" != \"\") {\r\n		$extFile1 	= substr(strrchr(\'REX_FILE[1]\', \'.\'), 1);\r\n		$iconFile1 	= $REX[\'HTDOCS_PATH\'].\'/redaxo/media/\'.parse_icon($extFile1);\r\n		$ooFile1 	= OOMedia::getMediaByName (\'REX_FILE[1]\');\r\n		$nameFile1 	= $ooFile1->getTitle();\r\n		$sizeFile1 	= Dateigroesse($REX[\'HTDOCS_PATH\'].\"files/REX_FILE[1]\");\r\n	\r\n		$out .= \'\r\n			<p class=\"link-download\">\r\n				<a href=\"\'.$REX[\'HTDOCS_PATH\'].\'files/REX_FILE[1]\" target=\"_blank\"><img src=\"\'.$iconFile1.\'\" alt=\"Datei zum herunterladen\" title=\"\'.$nameFile1.\' (\'.$sizeFile1.\')\" /></a>\r\n				<span class=\"name\">\'.$nameFile1.\'</span>\r\n				<span class=\"size\">(\'.$sizeFile1.\')</span>\r\n			</p>\';\r\n	}\r\n	\r\n	\r\n	print \'\r\n	<div class=\"com-tab com-tab-no-navi download\"><div class=\"com-tab-cntnt\"><div class=\"com-tab-cntnt-2\"><div class=\"com-tab-cntnt-3\">\'.$out.\'</div></div></div></div>\';\r\n \r\n?>','<br /><br />\r\nREX_MEDIA_BUTTON[1]\r\n<br /><br />\r\n\r\n','admin','','1212052159','0','','0');
INSERT INTO `rex_module` VALUES ('5','rex - X-Form','0','<?php\r\n\r\n// MODUL OUT - XO FORM\r\n// v0.16\r\n//--------------------------------------------------------------------------------\r\n\r\n$xform = new rex_xform;\r\nif (\"REX_VALUE[7]\" == 1) $xform->setDebug(TRUE);\r\n$form_data = \'REX_VALUE[3]\';\r\n$form_data = trim(str_replace(\"<br />\",\"\",rex_xform::unhtmlentities($form_data)));\r\n$xform->setFormData($form_data);\r\n$xform->setRedaxoVars(REX_ARTICLE_ID,REX_CLANG_ID); \r\n\r\n// Action/en\r\nif (\"REX_VALUE[10]\" != \"\") $xform->setGetdata(true); // Datein vorher auslesen ?\r\n$xform->setObjectparams(\"answertext\",\"REX_VALUE[6]\"); // Antworttext\r\n$xform->setObjectparams(\"main_table\",\"REX_VALUE[8]\"); // für db speicherungen und unique abfragen\r\n$xform->setObjectparams(\"form_type\",\"REX_VALUE[1]\"); // Aktion einstellen - Nihcts, Speichern, E-Mail, beides ? - form_typ\r\n// E-Mail Action\r\n$xform->setObjectparams(\"mail_subject\",\"REX_VALUE[4]\"); // Mailsubject\r\n$xform->setObjectparams(\"mail_body\",str_replace(\"<br />\",\"\",rex_xform::unhtmlentities(\'REX_VALUE[5]\'))); // Mailsubject\r\n$xform->setObjectparams(\"mail_to\",$REX[\'ERROR_EMAIL\']); // Mail to\r\nif (\"REX_VALUE[2]\" != \"\") $xform->setObjectparams(\"mail_from\",\"REX_VALUE[2]\"); // Mail from\r\nelse $xform->setObjectparams(\"mail_from\",$REX[\'ERROR_EMAIL\']); // Mail from\r\n\r\n// Individuelle Einstellungen\r\n?>REX_PHP_VALUE[9]<?php\r\n\r\n// Ausgabe\r\necho $xform->getForm();\r\n\r\n?>','<?php\r\n\r\n// MODUL IN - XO FORM\r\n// v0.19\r\n// --------------------------------------------------------------------------------\r\n\r\n?>\r\n\r\n<br />DebugModus:\r\n<?php\r\n\r\n$dbg = new rex_select();\r\n$dbg->setName(\"VALUE[7]\");\r\n$dbg->setSize(1);\r\n$dbg->setStyle(\"width:100px;\");\r\n$dbg->addOption(\"inakiv\",\"0\");\r\n$dbg->addOption(\"Aktiv\",\"1\");\r\n$dbg->setSelected(\"REX_VALUE[7]\");\r\necho $dbg->get();\r\n\r\n?>\r\n\r\n<br /><br />Formularbeschreibung:\r\n<br /><textarea name=\"VALUE[3]\" style=\"width:90%;height:180px;\">REX_VALUE[3]</textarea>\r\n\r\n<?php \r\nrex_xform::showHelp(); \r\n?>\r\n\r\n<br /><br /><b>Danksagung:</b>\r\n<br /><textarea name=\"VALUE[6]\" style=\"width:90%;height:100px;\">REX_VALUE[6]</textarea>\r\n\r\n<br /><br /><hr>\r\n\r\n<br /><b>Was soll passieren ?</b>\r\n<br /><br /><select name=\"VALUE[1]\" onchange=\"rex_xform_toggleDb(this);\" style=\"width:90%;\">\r\n<option value=\"\" <?php if(\"REX_VALUE[1]\" == \"\") echo \" selected \"; ?>>Nichts machen</option>\r\n<option value=\"0\" <?php if(\"REX_VALUE[1]\" == \"0\") echo \" selected \"; ?>>Nur in Datenbank speichern</option>\r\n<option value=\"1\" <?php if(\"REX_VALUE[1]\" == \"1\") echo \" selected \"; ?>>Nur E-Mail versenden</option>\r\n<option value=\"2\" <?php if(\"REX_VALUE[1]\" == \"2\") echo \" selected \"; ?>>E-Mail versenden und in Datenbank speichern</option>\r\n<option value=\"3\" <?php if(\"REX_VALUE[1]\" == \"3\") echo \" selected \"; ?>>E-Mail versenden und Datenbank abfragen</option>\r\n</select>\r\n\r\n<br /><br />\r\n<b>Daten zu Beginn aus der Datenbank holen</b>\r\n<select name=\"VALUE[10]\" style=\"width:90%;\">\r\n<option value=\"\" <?php if(\"REX_VALUE[10]\" == \"\") echo \" selected \"; ?>>Nein</option>\r\n<option value=\"Ja\" <?php if(\"REX_VALUE[10]\" == \"Ja\") echo \" selected \"; ?>>Ja</option>\r\n</select>\r\n\r\n<div id=\"db_data\">\r\n  <br />\r\n  <fieldset><legend>Datenbankdaten:</legend>\r\n  \r\n  <br /><b>Where Klausel angeben (PHP erlaubt)</b>\r\n  <br />$xform->setObjectparams(\"main_where\",$where);\r\n  \r\n  <br /><br /><textarea name=\"VALUE[9]\" cols=\"30\" rows=\"3\" style=\"width:90%; height:100px;\">REX_VALUE[9]</textarea>\r\n  \r\n  <div id=\"db_where_info\">\r\n    <table class=\"warning\"><tr><td>\r\n    In der Where Klausel koennen die verschiedenen Felder eingesetzt werden.\r\n    Dazu muss der entsprechende Feldname zwischen ### gesetzt werden. Z.B.\r\n    <br />text|myname|Name|1\r\n    <br />-> ###myname### als Platzhalter in der Mail\r\n    </td></tr></table>\r\n  </div>\r\n  \r\n  <br />\r\n  <br /><b>Bitte Tabelle angeben </b>\r\n  <br />(Diese Tabelle gilt auch bei Uniqueabfragen (Pflichtfeld=2) siehe oben\r\n  <br /><br /><?php\r\n  $gc = new rex_sql;\r\n  $gc->setQuery(\"SHOW TABLES\");\r\n  $tables = $gc->getArray();\r\n  \r\n  $ts = new rex_select;\r\n  $ts->setName(\"VALUE[8]\");\r\n  $ts->setSize(1);\r\n  $ts->addOption(\"Keine Tabelle ausgewählt\", \"\");\r\n  \r\n  foreach ($tables as $key => $value)\r\n  {\r\n    $ts->addOption(current($value), current($value));\r\n  }\r\n  \r\n  $ts->setSelected(\"REX_VALUE[8]\");\r\n  \r\n  echo $ts->show();\r\n  ?>\r\n  </legend>\r\n</div>\r\n\r\n\r\n\r\n<div id=\"mail_data\">\r\n  <br /> <br />\r\n  <fieldset><legend>Emailversandtdaten:</legend>\r\n  \r\n  <br /><b>Bitte folgende Daten eingeben</b>\r\n  <br /><br />Absender:\r\n  <br /><input type=\"text\" name=\"VALUE[2]\" value=\"REX_VALUE[2]\"  style=\"width:90%;\" />\r\n  \r\n  <br /><br />Subject:\r\n  <br /><input type=\"text\" name=\"VALUE[4]\" value=\"REX_VALUE[4]\"  style=\"width:90%;\" />\r\n  \r\n  <br /><br />Mailbody:\r\n  <br /><textarea name=\"VALUE[5]\" style=\"width:90%;height:100px;\">REX_VALUE[5]</textarea>\r\n  \r\n  <table class=\"warning\"><tr><td>\r\n  In der Mail koennen die verschiedenen Felder eingesetzt werden.\r\n  Dazu muss der entsprechende Feldname zwischen ### gesetzt werden. Z.B.\r\n  <br />text|myname|Name|1\r\n  <br />-> ###myname### als Platzhalter in der Mail\r\n  </td></tr></table>\r\n  \r\n  </fieldset>\r\n</div>\r\n\r\n<script type=\"text/javascript\">\r\n<!--\r\n\r\nfunction rex_xform_toggleDb(select)\r\n{\r\n   rex_xform_toggleDbValue(select.value);\r\n}\r\n\r\nfunction rex_xform_toggleDbValue(value)\r\n{\r\n   var displayDb = false;\r\n   var displayWhereInfo = false;\r\n   var displayMail = false;\r\n   \r\n   // \'\' => DefaultValue wenn Modul geadded wird\r\n   // 0 => Nur in Datenbank speichern\r\n   // 1 => Nur E-Mail versenden\r\n   // 2 => E-Mail versenden und in Datenbank speichern\r\n   if(value == 0 || value == \'\')\r\n   {\r\n      displayDb = true;\r\n   }\r\n   else if(value == 1)\r\n   {\r\n      displayMail = true;\r\n   }\r\n   else if(value == 2)\r\n   {\r\n      displayDb = true;\r\n      displayMail = true;\r\n   }\r\n   else if(value == 3)\r\n   {\r\n      displayDb = true;\r\n      displayWhereInfo = true;\r\n      displayMail = true;\r\n   }\r\n   \r\n   displayDb = displayDb == true ? \'\' : \'none\';\r\n   rex_xform_toggleElement(\'db_data\', displayDb);\r\n   \r\n   displayWhereInfo = displayWhereInfo == true ? \'\' : \'none\';\r\n   rex_xform_toggleElement(\'db_where_info\', displayWhereInfo);\r\n   \r\n   displayMail = displayMail == true ? \'\' : \'none\';\r\n   rex_xform_toggleElement(\'mail_data\', displayMail);\r\n}\r\n\r\n// Wenn der 2. Parameter angegeben wird, wird die style.display Eigenschaft auf den entsprechenden wert gesetzt,\r\n// Sonst wird der wert getoggled\r\nfunction rex_xform_toggleElement(id,display)\r\n{\r\n   var needle = new getObj(id);\r\n   \r\n   if (typeof(display) == \'undefined\')\r\n   {\r\n     needle.style.display = needle.style.display == \'\' ? \'none\' : \'\';\r\n   }\r\n   else\r\n   {\r\n     needle.style.display = display;\r\n   }\r\n}\r\n\r\nrex_xform_toggleDbValue(\"REX_VALUE[1]\");\r\n\r\n//-->\r\n</script>','admin','admin','1212070590','1212070874','','0');
INSERT INTO `rex_module` VALUES ('7','1001 - COM-Module - Tabbox','0','<?php\r\n$tab_arr = array(\r\n	\'REX_LINK_ID[1]\', \r\n	\'REX_LINK_ID[2]\', \r\n	\'REX_LINK_ID[3]\', \r\n	\'REX_LINK_ID[4]\', \r\n	\'REX_LINK_ID[5]\', \r\n	\'REX_LINK_ID[6]\', \r\n	);\r\n\r\n$tab_arr_in = array();\r\nforeach($tab_arr as $v)\r\n{\r\n	if ($a = OOArticle::getArticleById($v))\r\n	{\r\n		$tab_arr_in[$a->getName()] = $v;\r\n	}\r\n}	\r\n\r\n$tab_arr = array();\r\nforeach($tab_arr_in as $k => $v)\r\n{\r\n	if ($k != \"\" && $v != \"\")\r\n	{\r\n		$tab_arr[$k] = $v;\r\n	}\r\n}\r\n	\r\n$tab_g = rex_request(\'tab\',\'int\',\'\');	\r\nif ($tab_g < 0 || $tab_g >= count($tab_arr)) $tab_g = 0;\r\n			\r\n$tab_cnt = \'\';\r\n$tab_list = \'\';\r\n$tab_c = 0; // Counter\r\n// $tab_g -> active tab\r\nforeach ($tab_arr as $key => $val)\r\n{\r\n	$link = rex_getUrl(\'\', \'\', array(\'tab\' => $tab_c));\r\n	$tab_class = \'\';\r\n	\r\n	if ($tab_g == $tab_c) {\r\n		$tab_cnt = $val;\r\n		$tab_class = \'active \';\r\n	}\r\n	\r\n	if ($tab_c == 0)\r\n		$tab_class = \'tab-frst \';\r\n		\r\n	if ($tab_c == 0 AND $tab_g == $tab_c)\r\n		$tab_class = \'tab-frst-active \';\r\n		\r\n	if (($tab_c+1) == count($tab_arr))\r\n		$tab_class = \'tab-lst \';\r\n		\r\n	if (($tab_c+1) == count($tab_arr) AND $tab_g == $tab_c)\r\n		$tab_class = \'tab-lst-active \';\r\n		\r\n	if (count($tab_arr) == 1)\r\n		$tab_class = \'tab-aln\';\r\n	\r\n	$tab_c_active_nxt = $tab_g - 1;\r\n	if ($tab_c == $tab_c_active_nxt)\r\n		$tab_class .= \'active-nxt \';\r\n	\r\n	trim($tab_class);\r\n	if ($tab_class != \'\') {\r\n		$tab_class = \' class=\"\'.$tab_class.\'\"\';\r\n	}\r\n		\r\n	$tab_list .= \'<li\'.$tab_class.\'><a href=\"\'.rex_getUrl(\'\', \'\', array(\'tab\' => $tab_c)).\'\"><span>\'.$key.\'</span></a></li>\';\r\n	$tab_c++;\r\n}\r\n			\r\nprint \'\r\n	<div class=\"com-tab\">\r\n		<div class=\"com-tab-navi\">\r\n			<ul>\r\n				\'.$tab_list.\'\r\n			</ul>\r\n		</div>\r\n		\r\n		<div class=\"com-tab-cntnt\">\r\n		<div class=\"com-tab-cntnt-2\">\r\n		<div class=\"com-tab-cntnt-3\">\';\r\nif (!isset($REX[\'TMP\'][\'TAB_CID\']) || \"REX_ARTICLE_ID\" != $REX[\'TMP\'][\'TAB_CID\'])\r\n{\r\n  $REX[\'TMP\'][\'TAB_CID\'] = (int) $tab_cnt;\r\n  $ma = new rex_article;\r\n  $ma->setClang(REX_CLANG_ID);\r\n  if ($ma->setArticleId($tab_cnt)) echo $ma->getArticle();\r\n}else\r\n{\r\n  echo \"ERROR: ArticleinArticle\";\r\n}\r\nprint \'\r\n			<div class=\"clearer\"> </div>\r\n		</div>\r\n		</div>\r\n		</div>\r\n	</div>\';\r\n?>','Tab 1 Artikel:\r\n<br />REX_LINK_BUTTON[1]\r\n<br /><br />Tab 2 Artikel:\r\n<br />REX_LINK_BUTTON[2]\r\n<br /><br />Tab 3 Artikel:\r\n<br />REX_LINK_BUTTON[3]\r\n<br /><br />Tab 4 Artikel:\r\n<br />REX_LINK_BUTTON[4]\r\n<br /><br />Tab 5 Artikel:\r\n<br />REX_LINK_BUTTON[5]\r\n<br /><br />Tab 6 Artikel:\r\n<br />REX_LINK_BUTTON[6]\r\n<br /><br />','','admin','0','1212158041','','0');
INSERT INTO `rex_module` VALUES ('8','1002 - COM-Module - Usersuche','0','<?php\r\n// *********************** USERSUCHE\r\n$searchtext = rex_request(\"searchtext\",\"string\",\"\");\r\n$send = rex_request(\"send\",\"string\",\"\");\r\n$orderArr = array(\'firstname\' => \'Vorname\', \'name\' => \'Name\', \'email\' => \'E-Mail\');\r\n$orderArr = array(\'login\' => \'Login\');\r\n\r\n\r\n$ordersql = \'login\';\r\n$ordr = rex_request(\"ordr\",\"string\");\r\nif (isset($_REQUEST[\'ordr\']) AND (isset($orderArr[$ordr]))) {	\r\n	$ordersql = \'\'.$_REQUEST[\'ordr\'];\r\n}\r\n$ordersql = \' ORDER BY \'.$ordersql;\r\n\r\n// **** SQL AUFBAUEN\r\n$felder = array(\"login\",\"firstname\",\"name\",\"city\");\r\n$felder = array(\"login\");\r\n\r\n$addsql = \"\";\r\n$urlArr = array();\r\nif ($searchtext != \"\")\r\n{\r\n	foreach($felder as $feld)\r\n	{\r\n		if ($addsql != \"\") $addsql .= \' or \';\r\n		$addsql .= $feld.\' LIKE \"%\'.$searchtext.\'%\"\';\r\n	}\r\n	$addsql = \' and (\'.$addsql.\')\';\r\n	\r\n	$urlArr[\'searchtext\'] = htmlspecialchars(stripslashes($searchtext));\r\n}\r\n\r\n$filter = $_REQUEST[\"filter\"];\r\nif ($filter != \"\")\r\n{\r\n	$addsql = \'and login LIKE \"\'.$filter.\'%\"\';\r\n}\r\n\r\n$sql = \"select * from rex_com_user where status > 0 \".$addsql.$ordersql;\r\n\r\n$ms = new rex_sql;\r\n//$ms->debugsql = true;\r\n$ms->setQuery($sql);\r\n$sortLinks = \'\';\r\nforeach ($orderArr AS $key => $val) {\r\n	\r\n	$urlArr[\'ordr\'] = $key;\r\n	$sortLinks .= \'<a href=\"\'.rex_getUrl(\'\', \'\', $urlArr).\'\">\'.$val.\'</a> \';\r\n}\r\n\r\necho \'\r\n	<div class=\"com-usersearch\">\r\n		<form action=\"\'.rex_getUrl(REX_ARTICLE_ID,REX_CLANG_ID, array(\"send\"=>1),\"&\").\'\" method=\"post\" name=\"searchform\" id=\"searchform\">\r\n			\r\n			<p class=\"ftxt\">\r\n				<label for=\"usersearch\">Mitgliedsuche:</label>\r\n				<input type=\"text\" name=\"searchtext\" value=\"\'.htmlspecialchars(stripslashes($searchtext)).\'\" id=\"usersearch\" />\r\n			</p>\r\n			\r\n			<p class=\"link-save\">\r\n				<a href=\"javascript:void(0);\" onclick=\"document.getElementById(\\\'searchform\\\').submit();\"><span>Suche starten</span></a>\r\n			</p>\r\n			\r\n		</form>\';\r\n		\r\n\r\n// if ($ms->getRows()>0)\r\n//{\r\n	echo \'\r\n	<div class=\"com-tab com-tab-no-navi\">\r\n	<div class=\"com-tab-cntnt\">\r\n	<div class=\"com-tab-cntnt-2\">\r\n	<div class=\"com-tab-cntnt-3\">\r\n				\';\r\n				\r\n\r\n	// ***** Alle | A-Z | Blaettern\r\n	\r\n	$gn = \'select UPPER(SUBSTR(login,1,1)) as ch from rex_com_user where status > 0 group by ch\';\r\n	$ga = new rex_sql;\r\n	// $ga->debugsql = 1;\r\n	$ga->setQuery($gn);\r\n	$ga_array = $ga->getArray();\r\n	echo \'\r\n	<div class=\"com-navi\">\r\n			<ul class=\"navi com-navi-letters\">\r\n				<li><a href=\"\'.rex_getUrl(REX_ARTICLE_ID,REX_CLANG_ID).\'\">Alle | </a></li>\r\n				\';\r\n				foreach ($ga_array as $k => $v)\r\n				{\r\n					// echo \"<br />** $k ** \".$v[\"ch\"];\r\n					echo \'		<li><a href=\"\'.rex_getUrl(REX_ARTICLE_ID,REX_CLANG_ID, array(\"filter\"=>$v[\"ch\"])).\'\">\'.$v[\"ch\"].\'</a></li>\';\r\n				}\r\n				/*\r\n				for($i=65;$i<91;$i++)\r\n				{\r\n					echo \'		<li><a href=\"\'.rex_getUrl(REX_ARTICLE_ID,REX_CLANG_ID, array(\"filter\"=>chr($i))).\'\">\'.chr($i).\'</a></li>\';\r\n				}\r\n				*/\r\n	echo \'\r\n			</ul>\r\n			\'.rex_com_blaettern($ms).\'\r\n			<div class=\"clearer\"> </div>\r\n	</div>\r\n	\';\r\n				\r\n				\r\n	// ***** Sortieren\r\n	/*\r\n	echo \'\r\n				<div class=\"com-navi\">\r\n					<p class=\"flLeft\">Sortieren nach: \'.$sortLinks.\'</p>\r\n					\'.rex_com_blaettern(&$ms).\'\r\n					<div class=\"clearer\"> </div>\r\n				</div>\';\r\n	*/\r\n				\r\n				\r\n	\r\n	for($i=0;$i<$ms->getRows();$i++) \r\n	{			\r\n	\r\n		echo \'	<div class=\"com-contact\">\r\n					<div class=\"com-image\">\r\n						<p class=\"image\">\'.rex_com_showUser(&$ms, \"image\", \"\", TRUE, FALSE).\'</p>\r\n					</div>\r\n					\r\n					<div class=\"com-content\">\r\n					<div class=\"com-content-2\">\r\n						<p><span class=\"color-1\">\';\r\n		\r\n		echo rex_com_showUser(&$ms, \"login\", \"\", TRUE);\r\n		if ($ms->getValue(\"show_basisinfo\")==2)\r\n		{\r\n			echo \' [ \';\r\n			echo rex_com_showUser(&$ms, \"name\", \"\", TRUE);\r\n			echo \' ]\';\r\n		}\r\n		\r\n		echo \'</span></p>\';\r\n		if (rex_com_showUser(&$ms,\"motto\",\"\", FALSE)) echo \'<p>Motto: \'.rex_com_showUser(&$ms,\"motto\",\"\", FALSE).\'</p>\';\r\n		echo \'\r\n					</div>\r\n					</div>\r\n					<div class=\"clearer\"> </div>\r\n				</div>\';\r\n		\r\n		\r\n		$ms->next();\r\n				\r\n	}\r\n	echo \'\r\n				\r\n				<div class=\"clearer\"> </div>\r\n			</div>\r\n			</div>\r\n	\r\n		</div>\r\n		</div>\';\r\n// }\r\necho \'</div>\';\r\n?>','','','','0','0','','0');
