INSERT INTO `rex_module` VALUES ('10','1101 - COM-Module - Kontaktbox','0','<?php\r\n// ***** KONTAKTE\r\n$status = (int) \"REX_VALUE[1]\"; // 0=Bestaetigte/1=Unbestaetigte/2=Noch zu bestaetigen\r\n$aid = rex_request(\"article_id\",\"int\");\r\nif (isset($REX[\"COM_USER\"]) && is_object($REX[\"COM_USER\"]))\r\n{\r\n	$user_id = $REX[\"COM_USER\"]->getValue(\"rex_com_user.id\");\r\n	include $REX[\"INCLUDE_PATH\"].\"/addons/xform/classes/basic/class.rexform.inc.php\";\r\n	include $REX[\"INCLUDE_PATH\"].\"/addons/xform/classes/basic/class.rexlist.inc.php\";\r\n	include $REX[\"INCLUDE_PATH\"].\"/addons/xform/classes/basic/class.rexselect.inc.php\";\r\n	\r\n	$filter = $_REQUEST[\"filter\"];\r\n	$add_sql = \"\";\r\n	if ($filter != \"\")\r\n	{\r\n		$add_sql = \' AND rex_com_user.lastname LIKE \"\'.$filter.\'%\" \';\r\n	}\r\n	\r\n	\r\n	if ($status==0) $sql = \'\r\n		select \r\n			* \r\n		from \r\n			rex_com_contact,rex_com_user \r\n		where \r\n			rex_com_contact.to_user_id=rex_com_user.id \r\n			and rex_com_contact.user_id=\"\'.$user_id.\'\" \r\n			and accepted=1 \r\n			\'.$add_sql;\r\n	if ($status==1) $sql = \'\r\n		select \r\n			* \r\n		from \r\n			rex_com_contact,rex_com_user \r\n		where \r\n			rex_com_contact.to_user_id=rex_com_user.id and \r\n			rex_com_contact.user_id=\"\'.$user_id.\'\" and \r\n			rex_com_contact.accepted=0 and \r\n			rex_com_contact.requested=1 \r\n			\'.$add_sql;\r\n	if ($status==2) $sql = \'\r\n		select \r\n			* \r\n		from \r\n			rex_com_contact,rex_com_user \r\n		where \r\n			rex_com_contact.to_user_id=rex_com_user.id and \r\n			rex_com_contact.user_id=\"\'.$user_id.\'\" and \r\n			rex_com_contact.accepted=0 and \r\n			rex_com_contact.requested=0\r\n			\'.$add_sql;\r\n	\r\n	$gl = new rex_sql;\r\n	$gl->debugsql = 0;\r\n	$gl->setQuery($sql);\r\n	\r\n	$max = $gl->getRows();\r\n	$link = \'index.php?article_id=\'.$aid.\'&tab=\'.$status;\r\n	// Alle | A-Z | Blaettern\r\n	echo \'\r\n	<div class=\"com-navi\"><p class=\"flLeft\">&nbsp;</p>\r\n	<!--		<ul class=\"navi com-navi-letters\">\r\n				<li><a href=\"\'.$link.\'\">Alle | </a></li>\r\n				\';\r\n	\r\n	for($i=65;$i<91;$i++)\r\n	{\r\n		echo \'		<li><a href=\"\'.$link.\'&amp;filter=\'.chr($i).\'\">\'.chr($i).\'</a></li>\';\r\n	}\r\n	echo \'\r\n			</ul>\r\n	-->\r\n					\'.rex_com_blaettern(&$gl).\'\r\n			<div class=\"clearer\"> </div>\r\n	</div>\r\n	\';\r\n\r\n	// Ergebnisse anzeigen\r\n	for($i=0;$i<$gl->getRows();$i++)\r\n	{\r\n		echo \'\r\n			<div class=\"com-contact\">\r\n				<div class=\"com-image\">\r\n					<p class=\"image\">\'.rex_com_showUser(&$gl,\"image\",\"\", TRUE).\'</p>\r\n				</div>\r\n				<div class=\"com-content\">\r\n				<div class=\"com-content-2\">\r\n					<p><span class=\"color-1\">\'.rex_com_showUser(&$gl,\"name\",\"\", TRUE).\', \'.rex_com_showUser(&$gl,\"city\",\"\", FALSE).\'</span><br />Bestätigt am: \'.rex_com_formatter($gl->getValue(\"create_datetime\"),\'date\').\'</p>\r\n					\';\r\nif(rex_com_showUser(&$gl,\"motto\",\"\", FALSE) != \"\") echo \'<p>Motto: \'.rex_com_showUser(&$gl,\"motto\",\"\", FALSE).\'</p>\';\r\necho \'\r\n					<p class=\"link-button\"><a href=\"\'.rex_com_showUser(&$gl,\"url\").\'\"><span>Mehr Infos</span></a></p>\r\n				</div></div>\r\n				<div class=\"clearer\"> </div>\r\n			</div>\r\n		\';\r\n		$gl->next();\r\n	}\r\n	echo \'<div class=\"clearer\"> </div>\';\r\n}\r\n?>','<?php\r\n$g = new rex_select;\r\n$g->setName(\"VALUE[1]\");\r\n$g->addOption(\"Bestätigte Kontakte\",0);\r\n$g->addOption(\"Unbestätigte Kontakte\",1);\r\n$g->addOption(\"Zu bestätigende Kontakte\",2);\r\n$g->setSelected(\"REX_VALUE[1]\");\r\n$g->setSize(1);\r\necho $g->show();\r\n?><br />\r\n','','admin','0','1212189195','','0');
INSERT INTO `rex_module` VALUES ('14','1301 - COM-Module - Nachrichten','0','<?php\r\n// ----- Meine Nachrichten\r\n$tab = rex_request(\"tab\",\"int\",\"0\");\r\n$aid = rex_request(\"article_id\",\"int\",\"0\");\r\nif (isset($REX[\"COM_USER\"]) && is_object($REX[\"COM_USER\"]))\r\n{\r\n	if (\"REX_VALUE[1]\" == \"1\")\r\n	{\r\n		// ***** outbox\r\n		 // von mir an andere\r\n		 // -> anderen user anzeigen\r\n		$who = \"from_user_id\";\r\n		$text = \"Zu\";\r\n		$show = \"to_user_id\"; // diesen user anzeigen\r\n	}else\r\n	{\r\n		// ***** inbox\r\n		// an mich\r\n		// -> user von anzeigen\r\n		$who = \"to_user_id\"; // an mich\r\n		$text = \"Von\";\r\n		$show = \"from_user_id\"; // diesen user anzeigen\r\n	}\r\n	$user_id = $REX[\"COM_USER\"]->getValue(\"id\");\r\n	\r\n	if($_REQUEST[\'func\'] == \"delete\")\r\n	{\r\n	\r\n		$del_id = (int) $_REQUEST[\'del_id\'];\r\n		$delsql = new rex_sql();\r\n		$delsql->setQuery(\"DELETE FROM `rex_com_message` WHERE (`user_id`=\'\".$user_id.\"\') AND `id`=\'\".$del_id.\"\'\");	\r\n		echo \'<p class=\"warning\">Nachricht wurde gelöscht.</p>\';\r\n	\r\n	}\r\n	\r\n	$sql = \"SELECT \r\n		* \r\n		FROM \r\n			rex_com_message, rex_com_user\r\n		WHERE \r\n			rex_com_message.user_id=\".$user_id .\"\r\n			AND rex_com_message.$who=\".$user_id .\"\r\n			AND rex_com_message.$show=rex_com_user.id \r\n        ORDER BY \r\n        	rex_com_message.create_datetime DESC\";\r\n\r\n	$link = \"index.php?article_id=$aid&tab=$tab\";\r\n	$m = new rex_sql;\r\n	// $m->debugsql = 1;\r\n	$m->setQuery($sql);\r\n	// Ergebnisse anzeigen\r\n	for($i=0;$i<$m->getRows();$i++)\r\n	{\r\n		echo \'\r\n			<div class=\"com-contact\">\r\n				<div class=\"com-image\">\r\n					<p class=\"image\">\'.rex_com_showUser(&$m,\"image\",\"\", TRUE, FALSE).\'</p>\r\n				</div>\r\n				<div class=\"com-content\">\r\n					<p><span class=\"color-1\">\'.rex_com_showUser(&$m,\"name\",\"\", TRUE).\', \'.rex_com_showUser(&$m,\"city\",\"\", FALSE).\'</span><br />Erstellt am: <b>\'.rex_formatter($m->getValue(\"create_datetime\"),\"datetime\",\"timestamp\").\'</b></p>\r\n					<p>\r\n					Betreff: <b>\'.htmlspecialchars($m->getValue(\"rex_com_message.subject\")).\'</b>\r\n					<br />Nachricht: \r\n					<br /><b>\'.nl2br(htmlspecialchars($m->getValue(\"rex_com_message.body\"))).\'</b></p>\r\n					<p class=\"link-button\"><a href=\"\'.$link.\'&amp;func=delete&del_id=\'.$m->getValue(\"rex_com_message.id\").\'\"><span>Löschen</span></a></p>\r\n				</div>\r\n				<div class=\"clearer\"> </div>\r\n			</div>\r\n		\';\r\n		$m->next();\r\n	}\r\n\r\n	if ($m->getRows()<1)\r\n	{\r\n		echo \'<p class=\"nomessage\">Es befinden sich derzeit keine aktuellen Nachrichten in Ihrer Nachrichtenbox.</p>\';\r\n	}\r\n	echo \'<div class=\"clearer\"> </div>\';\r\n}\r\n?>','Outbox = 1\r\nWelche Box soll angezeigt werden:\r\n<br /><?php\r\n$sel = new rex_select;\r\n$sel->setName(\"VALUE[1]\");\r\n$sel->setSize(1);\r\n$sel->addOption(\"Inbox\",\"0\");\r\n$sel->addOption(\"Outbox\",\"1\");\r\n$sel->setSelected(\"REX_VALUE[1]\");\r\necho $sel->show();\r\n?><br />\r\n','','admin','0','1212189456','','0');
INSERT INTO `rex_module` VALUES ('18','1501 - COM-Module - Artikelkommentar','0','<div class=\"rex-com-articlecommentbox\">\r\n<?php\r\n$ADMIN = FALSE;\r\nif (isset($REX[\"COM_USER\"]) && is_object($REX[\"COM_USER\"]))\r\n{\r\n	if ($REX[\"COM_USER\"]->getValue(\"admin\")==1) $ADMIN = TRUE; \r\n	if ($ADMIN && isset($_REQUEST[\"delete_message\"]) && $_REQUEST[\"delete_message\"]==1)\r\n	{\r\n		$msg_id = (int) $_REQUEST[\"msg_id\"];\r\n		$d = new rex_sql;\r\n		$d->setQuery(\'update rex_com_comment set status=0 where article_id=REX_ARTICLE_ID AND id=\'.$msg_id);\r\n	}\r\n	$xform = new rex_xform;\r\n	$xform->setDebug(FALSE);\r\n	$form_data = \'\r\nhtml|<div class=\"com-tab com-tab-no-navi\">\r\nhtml|<div class=\"com-tab-cntnt\">\r\nhtml|<div class=\"com-tab-cntnt-2\">\r\nhtml|<div class=\"com-tab-cntnt-3\">\r\nhtml|<div id=\"rex-form\">\r\nhtml|<div class=\"spcl-bgcolor\">\r\nhidden|article_id|REX_ARTICLE_ID\r\nhidden|status|1\r\ncom_user|user_id|id|Absender\r\ntextarea|comment|Kommentar\r\ntimestamp|create_datetime\r\nvalidate|notEmpty|comment|Bitte gib einen Kommentar ein.\r\naction|db|rex_com_comment\r\nobjparams|submit_btn_show|0\r\nsubmit|submit|Abschicken|no_db\r\nhtml|<div class=\"clearer\"> </div>\r\nhtml|</div>\r\nhtml|</div>\r\nhtml|</div>\r\nhtml|</div>\r\nhtml|</div>\r\nhtml|</div>\r\n	\';\r\n	\r\n	$form_data = trim(str_replace(\"<br />\",\"\",rex_xform::unhtmlentities($form_data)));\r\n	\r\n	$xform->setFormData($form_data);\r\n	$xform->setRedaxoVars(REX_ARTICLE_ID,REX_CLANG_ID); \r\n	$xform->setObjectparams(\"answertext\",\"Vielen Dank für den Eintrag\"); // Antworttext\r\n	$xform->setObjectparams(\"main_table\",\"rex_com_comment\"); // für db speicherungen und unique abfragen\r\n	\r\n	// Aktion einstellen\r\n	$xform->setObjectparams(\"form_type\",-1); // form_typ\r\n	$addcomment = $xform->getForm();\r\n}else\r\n{\r\n	$addcomment = \'<div class=\"nologin\"><p>Sie sind nicht eingeloggt. Bitte melden Sie sich an, wenn Sie Kommentare schreiben wollen</p></div>\';\r\n}\r\n// ***** SHOW MESSAGES\r\necho \'\r\n<div class=\"com-tab com-tab-no-navi\">\r\n<div class=\"com-tab-cntnt\">\r\n<div class=\"com-tab-cntnt-2\">\r\n<div class=\"com-tab-cntnt-3\">\';\r\n\r\n$commentsql = new rex_sql();\r\n$commentsql->debugsql = 0;\r\n$commentsql->setQuery(\"SELECT * \r\n	FROM  rex_com_comment \r\n	LEFT JOIN rex_com_user ON rex_com_comment.user_id=rex_com_user.id \r\n	WHERE rex_com_comment.article_id=REX_ARTICLE_ID  and rex_com_comment.status=1\r\n	ORDER BY rex_com_comment.create_datetime desc\");\r\nif($commentsql->getRows()<=0)\r\n{\r\n	echo \'<p class=\"com-whitebox\">Kein Kommentar vorhanden !</p>\';\r\n}else\r\n{\r\n	$cl = \"\";\r\n	for($i=0;$i<$commentsql->getRows();$i++)\r\n	{\r\n		// $cl\r\n		echo \'\r\n		<div class=\"com-comment\">\r\n			<div class=\"com-image\">\r\n				<p class=\"image\">\'.rex_com_showUser(&$commentsql,\"image\").\'</p>\r\n			</div>\r\n			\r\n			<div class=\"com-content-name\">\r\n				<p><span class=\"color-1\">\'.rex_com_showUser(&$commentsql,\"name\").\'</span>\r\n					<br />\'.rex_com_formatter($commentsql->getValue(\"rex_com_comment.create_datetime\"),\'datetime\').\'\r\n				</p>\r\n			</div>\r\n			<div class=\"com-content\">\r\n				<p><b>\'.nl2br(htmlspecialchars($commentsql->getValue(\"rex_com_comment.comment\"))).\'</b></p>\';\r\n			\r\n			if ($commentsql->getValue(\"rex_com_user.motto\") != \'\')\r\n				echo \'<p>Motto: \'.$commentsql->getValue(\"rex_com_user.motto\").\'</p>\';\r\n	\r\n		if ($ADMIN)\r\n		{\r\n			$link_params = array_merge($params,array(\"article_id\"=>REX_ARTICLE_ID,\"delete_message\"=>1,\"msg_id\"=>$commentsql->getValue(\"rex_com_comment.id\")));\r\n			echo \'<p class=\"link-button\"><a href=\"\'.rex_getUrl(REX_ARTICLE_ID,\'\',$link_params).\'\"><span>Löschen</span></a></p>\';\r\n		}\r\n		echo \'</div>\r\n			<div class=\"clearer\"> </div>\r\n		</div>\';\r\n		\r\n		if ($cl == \"\") $cl = \' class=\"alternative\"\';\r\n		else $cl = \"\";\r\n		$commentsql->next();\r\n	}\r\n}\r\necho \'\r\n<div class=\"clearer\"> </div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n\';\r\necho $addcomment;\r\n?>\r\n</div>','','','admin','0','1212169818','','0');
INSERT INTO `rex_module` VALUES ('16','1401 - COM-Module - Board','0','<div class=\"com-board\"><?php\r\n$board = new rex_com_board;\r\nif (isset($REX[\"COM_USER\"]) && $REX[\"COM_USER\"]->getValue(\"admin\")==1) $board->setAdmin(true);\r\n$board->setArticleId(\"REX_ARTICLE_ID\");\r\n$board->setBoardname(\"REX_VALUE[1]\");\r\n$board->setRealBoardname(\"REX_VALUE[2]\");\r\nif (\"REX_VALUE[7]\" != \"user\") $board->setAnonymous(true);\r\nelseif (isset($REX[\"COM_USER\"]) && $REX[\"COM_USER\"]->getValue(\"status\")>0) $board->setUser($REX[\"COM_USER\"]->getValue(\"login\"));\r\n\r\n$board->setFormPostAction(\"index.php\");\r\necho $board->showBoard();\r\n?></div>\r\n','Boardbezeichnung/ Allgemeiner Titel:\r\n<br /><input type=text size=50 name=\"VALUE[2]\" value=\"REX_VALUE[2]\" />\r\n<br /><br />Datenbankbezeichnung (nur kleine Buchstaben verwenden):\r\n<br /><input type=text size=50 name=\"VALUE[1]\" value=\"REX_VALUE[1]\" />\r\n<br /><br /><?\r\n$se = new rex_select;\r\n$se->setName(\"VALUE[7]\");\r\n$se->setSize(1);\r\n$se->addOption(\"Anonym\",\"anonym\");\r\n$se->addOption(\"Nur eingeloggte User\",\"user\");\r\n$se->setSelected(\"REX_VALUE[7]\");\r\necho $se->show();\r\n?><br /><br />','','admin','0','1212571498','','0');
INSERT INTO `rex_module` VALUES ('17','1402 - COM-Module - Boardteaser','0','<?php\r\nif(OOAddon::isAvailable(\'textile\'))\r\n{\r\n	$text = \'\';\r\n	if(REX_IS_VALUE[1])\r\n	{\r\n	\r\n$input =<<< EOT123451827235\r\nREX_HTML_VALUE[1]\r\nEOT123451827235;\r\n	\r\n		$board_id = \'REX_VALUE[2]\';\r\n		$gt = new rex_sql();\r\n		$gt->debugsql = 0;\r\n		$gt->setQuery(\'select count(message_id) as c from rex_com_board where board_id=\"REX_VALUE[2]\" and status=1\');\r\n		$input = str_replace(\"###board-eintraege###\",$gt->getValue(\"c\"),$input);\r\n		$gt->setQuery(\'select count(message_id) as c from rex_com_board where board_id=\"REX_VALUE[2]\" and status=1 and re_message_id=0\');\r\n		$input = str_replace(\"###board-themen###\",$gt->getValue(\"c\"),$input);\r\n		// $text = str_replace(\"###\",\"&#x20;\",$text);\r\n		$textile = new Textile(); \r\n		$input = $textile->TextileThis($input);\r\n	} \r\n	\r\n	print \'<div class=\"txt-img rex-com-boardteaser\">\'. $input. \'</div>\';\r\n}else\r\n{\r\n	echo rex_warning(\'Dieses Modul benötigt das \"textile\" Addon!\');\r\n}\r\n?>','<strong>Text</strong>\r\n<br />[Für Anzahl der Einträge bitte ###board-eintraege### verwenden]:\r\n<br />[Für Anzahl der Themen bitte ###board-themen### verwenden]:\r\n<br />\r\n<?php if(OOAddon::isAvailable(\'textile\')) { ?>\r\n<textarea name=\"VALUE[1]\" cols=\"80\" rows=\"10\" class=\"inp100\">REX_VALUE[1]</textarea>\r\n<br />\r\n<?php rex_a79_help_overview(); }else { echo rex_warning(\'Dieses Modul benötigt das \"textile\" Addon!\'); } ?>\r\n<br />Boardkey:\r\n<input type=\"text\" name=\"VALUE[2]\" value=\"REX_VALUE[2]\" size=\"20\" />\r\n<br /><br />','','','0','0','','0');
INSERT INTO `rex_template` VALUES ('1','','1011 - COM-Template - Standardtemplate','<?php\r\nerror_reporting(E_ALL ^ E_NOTICE);\r\n\r\n// Die IDs der Templates muessen angepasst werden\r\n// Permission Funktion includen, passende ID einsetzen\r\n?>REX_TEMPLATE[9]<?php\r\n\r\n// Authentifizierung includen, passende ID einsetzen\r\n?>REX_TEMPLATE[8]<?php\r\n\r\nif (rex_com_checkUserPerm($this->getValue(\"art_com_perm\")))\r\n{\r\n  // Zugriff erlaubt\r\n  $CONTENT  = $this->getArticle(1);\r\n  $CONTENT_LEFT  = $this->getArticle(2);\r\n  $CONTENT_RIGHT  = $this->getArticle(3);\r\n	$art = OOArticle::getArticleById(REX_ARTICLE_ID);\r\n	$cat = OOCategory::getCategoryById($art->getCategoryId());\r\n	if ($CONTENT_LEFT == \'\') {\r\n		while ($CONTENT_LEFT == \'\') {\r\n			if ($cat == null) break;\r\n			$a = new rex_article($cat->getId());\r\n			$CONTENT_LEFT = $a->getArticle(2);\r\n			$cat = $cat->getParent();\r\n		} \r\n	}\r\n	if ($CONTENT_LEFT == \'\') {\r\n		$a = new rex_article($REX[\'START_ARTICLE_ID\']);\r\n		$CONTENT_LEFT = $a->getArticle(2);\r\n	}\r\n	if ($CONTENT_RIGHT == \'\') {\r\n		while ($CONTENT_RIGHT == \'\') {\r\n			if ($cat == null) break;\r\n			$a = new rex_article($cat->getId());\r\n			$CONTENT_RIGHT = $a->getArticle(3);\r\n			$cat = $cat->getParent();\r\n		} \r\n	}\r\n	if ($CONTENT_RIGHT == \'\') {\r\n		$a = new rex_article($REX[\'START_ARTICLE_ID\']);\r\n		$CONTENT_RIGHT = $a->getArticle(3);\r\n	}\r\n}else\r\n{\r\n  // Zugriff verboten\r\n  ob_end_clean();\r\n  ob_end_clean();\r\n  header(\'Location:\'.rex_getUrl(REX_COM_PAGE_LOGIN_ID,\'\',array(\"errmsg\"=>\'Sie haben keine Rechte für diese Seite und wurden deswegen auf die Startseite geleitet.\')));\r\n  exit;\r\n}\r\n?><!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Strict//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-strict.dtd\">\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\" xml:lang=\"de\" lang=\"de\">\r\n<head>\r\n	<title>Community REDAXO</title>\r\n	<meta http-equiv=\"content-type\" content=\"te