INSERT INTO `rex_template` VALUES ('8','','1012 - COM-Template - Authentifizierung','<?php\r\n// -------------------------------------------------------------- USER AUTH\r\n\r\nunset($REX[\'COM_USER\']);\r\n$pagekey = \'comrex\'; // Frontendkey, muss sich unterscheiden, damit frontend und backend sich nicht schneiden.\r\n\r\n$login_name = rex_request(\"login_name\",\"string\");\r\n$login_psw = rex_request(\"login_psw\",\"string\");\r\n$logout = rex_request(\"logout\",\"int\");\r\n$msg = \'Bitte einloggen\';\r\n\r\n// ----- session start\r\nsession_start();\r\n$COM_USER_SAVE = new rex_sql();\r\n$COM_USER_SAVE->setTable(\'rex_com_user\');\r\nif ((isset($_SESSION[$pagekey][\'UID\']) && $_SESSION[$pagekey][\'UID\'] != \"\") or $login_name != \"\" or $login_psw != \"\")\r\n{\r\n	$user_id = (int) $_SESSION[$pagekey][\'UID\'];\r\n	$GLOBALS[\"I18N\"] = rex_create_lang(\"de\");\r\n	$REX[\'COM_USER\'] = new rex_login();\r\n	$REX[\'COM_USER\']->setSqlDb(1);\r\n	$REX[\'COM_USER\']->setSysID($pagekey);\r\n	$REX[\'COM_USER\']->setSessiontime(3000);\r\n	$REX[\'COM_USER\']->setLogin($login_name,$login_psw);\r\n	if ($logout == 1) { $REX[\'COM_USER\']->setLogout(true); }\r\n	$REX[\'COM_USER\']->setUserID(\"rex_com_user.id\");\r\n	$REX[\'COM_USER\']->setUserquery(\"select * from rex_com_user where id=\'USR_UID\' and status>0\");\r\n	$REX[\'COM_USER\']->setLoginquery(\"select * from rex_com_user where login=\'USR_LOGIN\' and password=\'USR_PSW\' and status>0\");\r\n	\r\n	if ($REX[\'COM_USER\']->checkLogin())\r\n	{\r\n		// ----- Login gelungen\r\n		if ($login_name != \"\")\r\n		{\r\n			// ----- Login gelungen und gerade erst eingeloggt\r\n			// -> last_xs\r\n			$msg = \'Sie haben sich eingeloggt!\';\r\n			$COM_USER_SAVE->setValue(\'last_login\',time());\r\n			$jump_aid = $REX[\'START_ARTICLE_ID\'];\r\n		}\r\n\r\n	}else\r\n	{\r\n		$ud = new rex_sql;\r\n		// $ud->debugsql = 1;\r\n		if ($user_id != 0 || $logout == 1) $ud->setQuery(\'update rex_com_user set online_status=0 where id=\"\'.$user_id.\'\"\');\r\n		\r\n		\r\n		// ----- Login failed\r\n		$msg = \'Login ist fehlgeschlagen.\';\r\n		if ($logout == 1) $msg = \'Sie haben sich ausgeloggt\';\r\n		\r\n		unset($REX[\'COM_USER\']);\r\n		if ($logout == 1) $jump_aid = $REX[\'START_ARTICLE_ID\'];\r\n	}\r\n}else\r\n{\r\n	// ----- nicht eingeloggt und kein login\r\n	$msg = \'Sie sind nicht eingeloggt.\';\r\n	unset($REX[\'COM_USER\']);\r\n}\r\nif (isset($REX[\'COM_USER\']) && is_object($REX[\'COM_USER\']) && $REX[\'COM_USER\']->getValue(\'rex_com_user.id\')!=\'\')\r\n{\r\n	// ----- session speichern\r\n	\r\n	$COM_USER_SAVE->setValue(\'online_status\',\'1\');\r\n	$COM_USER_SAVE->setValue(\'session_id\',session_id());\r\n	$COM_USER_SAVE->setValue(\'last_xs\',time());\r\n	$COM_USER_SAVE->setWhere(\'id=\'.$REX[\'COM_USER\']->getValue(\'rex_com_user.id\'));\r\n	$COM_USER_SAVE->update();\r\n}\r\nunset($COM_USER_SAVE);\r\n\r\nif (isset($jump_aid))\r\n{\r\n  header(\'Location:\'.rex_getUrl($jump_aid));\r\n  exit;\r\n}\r\n\r\n?>','0','admin','admin','1212572400','1212185314','a:1:{s:5:\"ctype\";a:0:{}}','0');
INSERT INTO `rex_template` VALUES ('9','','1013 - COM-Template - Permission/Rechte','<?php\r\n/*\r\n   	 0  Standard   	Zugriff für alle \r\n  	-1 	Zugriff für nicht eingeloggte User  \r\n  	 1 	Zugriff für eingeloggte User  \r\n  	 2 	Zugriff für eingeloggte Moderatoren und Admins\r\n  	 3 	Zugriff für eingeloggte Admins\r\n*/\r\nfunction rex_com_checkUserPerm($type,$group = \"\")\r\n{\r\n	global $REX;\r\n	if ($type == \"\") return true; // Zugriff für alle \r\n	if ($type == \"0\") return true; // Zugriff für alle \r\n  \r\n	if (isset($REX[\'COM_USER\']) && is_object($REX[\'COM_USER\']))\r\n	{\r\n		if ($type == \"1\") return true; // Zugriff für eingeloggte User\r\n		if ($type == \"2\" && ($REX[\'COM_USER\']->getValue(\"admin\")==1 || $REX[\'COM_USER\']->getValue(\"moderator\")==1)) return true;\r\n		if ($type == \"3\" && $REX[\'COM_USER\']->getValue(\"admin\")==1) return true;\r\n	}\r\n	if(!isset($REX[\'COM_USER\']) || !is_object($REX[\'COM_USER\']))\r\n	{\r\n		if ($type == \"-1\") return true; // Zugriff für nicht eingeloggte User \r\n	} \r\n	return false;\r\n}\r\n	\r\n?>','0','admin','admin','1212572401','1212185586','a:1:{s:5:\"ctype\";a:0:{}}','0');
INSERT INTO `rex_template` VALUES ('10','','1014 - COM-Template - Userloginfenster','<?php\r\n// ********************************************************* LOGIN\r\n$login = \'\';\r\n$status = \'\';\r\n$now = time();\r\n$gab = 60*60;\r\n$ti = $now-$gab;\r\n// User sind online\r\n$gu = new rex_sql;\r\n$gu->setQuery(\'select * from rex_com_user where last_xs>\'.$ti.\' and online_status=1\');\r\n$user_online = $gu->getRows();\r\nif ($user_online == 0) $user_online_text = \'Kein Benutzer ist online\'; \r\nelseif ($user_online < 2) $user_online_text = \'Ein Benutzer ist online\';\r\nelse $user_online_text = $user_online.\' Benutzer sind online\';\r\n$users_online = \"\";\r\n$users_online = \'<ul class=\"com-usr-list\">\';\r\nfor($i=0;$i<$gu->getRows();$i++)\r\n{\r\n  //if ($users_online != \"\") $users_online .= \' + \';\r\n  $users_online .= \'<li>\'.rex_com_showUser($gu, \"login\", \"\", TRUE).\'</li>\';\r\n  $gu->next();\r\n}\r\nif ($gu->getRows()==0) $users_online .= \'<li>Kein User ist online</li>\';\r\n$users_online .= \'</ul>\';\r\nif (isset($REX[\'COM_USER\']) && is_object($REX[\'COM_USER\']))\r\n{\r\n	$status = \'Login\';\r\n\r\n	$gu->setQuery(\'select count(id) from rex_com_contact where user_id=\'.$REX[\'COM_USER\']->getValue(\'id\').\' and accepted=1\');\r\n	// $gu->debugsql = 1;\r\n	$user_contacts = $gu->getValue(\"count(id)\");\r\n	$gu->setQuery(\'select count(id) from rex_com_contact where user_id=\'.$REX[\'COM_USER\']->getValue(\'id\').\' and accepted=0 and requested=0\');\r\n	$user_contact_requests = $gu->getValue(\"count(id)\");\r\n	$login .= \'<div id=\"com-user-box\">\';\r\n	$login .= \'<h4>User</h4>\';\r\n	//$login .= \'<p>Sie sind eingeloggt als:<br /> <strong>\'.$REX[\'COM_USER\']->getValue(\'login\').\'</strong></p>\';\r\n	$login .= \'<p><strong>\'.$REX[\'COM_USER\']->getValue(\'login\').\'</strong></p>\';\r\n	$login .= \'<ul>\r\n					<li><a class=\"icon icon-myprfl\" href=\"\'.rex_getUrl(REX_COM_PAGE_MYPROFIL_ID).\'\"><span>Mein Profil</span></a></li>\r\n					<li><a class=\"icon icon-lgt\" href=\"\'.rex_getUrl($logout_aid,\'\',array(\'logout\'=>1)).\'\"><span>Logout</span></a></li>\r\n				</ul>\r\n			\';\r\n	\r\n	//$login .= \'<p><br /><a href=\"\'.rex_getUrl(REX_COM_PAGE_MYPROFIL_ID).\'\">&raquo; Mein Profil</a></p>\';\r\n	//$login .= \'<p class=\"logout\"><a href=\"\'.rex_getUrl($logout_aid,\'\',array(\'logout\'=>1)).\'\">&raquo; Logout</a></p>\';\r\n	\r\n	$login .= \'<div class=\"splt\"></div>\';\r\n	$login .= \'<ul>\';\r\n	$login .= \'<li>\'.$user_online_text.\'</li>\';\r\n	if ($user_contacts == 1) $login .= \'<li>Sie haben einen Kontakt</li>\';\r\n	elseif ($user_contacts == 0) $login .= \'<li>Sie haben keinen Kontakt</li>\';\r\n	else $login .= \'<li>Sie haben \'.$user_contacts.\' Kontakte</li>\';\r\n	if ($user_contact_requests == 1) $login .= \'<li>Sie haben eine Kontaktanfrage</li>\';\r\n	elseif ($user_contact_requests == 0) $login .= \'<li>Sie haben keine Kontaktanfrage</li>\';\r\n	else $login .= \'<li>Sie haben \'.$user_contact_requests.\' Kontaktanfragen</li>\';\r\n	// $login .= \'<li>Aktivität: \'.$REX[\'COM_USER\']->getValue(\'activity\').\'%</li>\';\r\n	$login .= \'</ul>\';\r\n	\r\n	$login .= \'<div class=\"splt\"></div>\';\r\n	$login .= \'<h4>Folgende User sind online:</h4>\'.$users_online;\r\n	$login .= \'</div>\';\r\n}else\r\n{\r\n	$status = \'Logout\';\r\n	$login .= \'<div id=\"com-user-box\">\';\r\n	$login .= \'<h4>Login</h4>\';\r\n	$login .= \'<form action=\"index.php\" method=\"post\">\r\n				<input type=\"hidden\" name=\"article_id\" value=\"\'.REX_COM_PAGE_LOGIN_ID.\'\" />\r\n					<fieldset>\r\n					<p class=\"formtext\">\r\n						<label for=\"name\" class=\"hidden\">Benutzername:</label>\r\n						<input type=\"text\" id=\"name\" name=\"login_name\" value=\"Benutzername...\" onblur=\"if(this.value == \\\'\\\') this.value=\\\'Benutzername...\\\'\" onfocus=\"if(this.value == \\\'Benutzername...\\\') this.value=\\\'\\\'\" />\r\n					</p>\r\n					<p class=\"formtext\">\r\n						<label for=\"password\" class=\"hidden\">Passwort:</label>\r\n						<input type=\"password\" id=\"password\" name=\"login_psw\" value=\"Passwort...\" onblur=\"if(this.value == \\\'\\\') this.value=\\\'Passwort...\\\'\" onfocus=\"if(this.value == \\\'Passwort...\\\') this.value=\\\'\\\'\" />\r\n					</p>\r\n					<!-- <p class=\"formcheckbox\">\r\n						<input type=\"checkbox\" id=\"loginsave\" name=\"login_save\" />\r\n						<label for=\"loginsave\">Login speichern</label>\r\n					</p>-->\r\n					<p class=\"formsubmit\">\r\n						<input class=\"submit\" type=\"submit\" value=\"Login\" title=\"Anmeldung durchführen\" />\r\n					</p>\r\n					</fieldset>\r\n				</form>\r\n				\r\n				<ul>\r\n					<li><a class=\"icon icon-rgstr\" href=\"\'.rex_getUrl(REX_COM_PAGE_REGISTER_ID).\'\"><span>Registrieren ?</span></a></li>\r\n					<li><a class=\"icon icon-psswd-frgttn\" href=\"\'.rex_getUrl(REX_COM_PAGE_PSWFORGOTTEN_ID).\'\"><span>Passwort ?</span></a></li>\r\n				</ul>\r\n				\r\n				<div class=\"splt\"></div>\r\n				\r\n				<h4>Folgende User sind online:</h4>\r\n				\'.$users_online.\'\r\n			\';\r\n	$login .= \'</div>\';\r\n\r\n} \r\necho \'<div class=\"bx-v1 bx-shdw\"><div class=\"bx-v1-2 bx-shdw-2\"><div class=\"bx-v1-cntnt\"><h3><strong>Status</strong> / \'.$status.\'</h3>\'.$login.\'</div></div></div>\';\r\n?>','0','admin','admin','1212572402','1212185635','a:1:{s:5:\"ctype\";a:0:{}}','0');
