INSERT INTO `rex_template` VALUES ('12','','1015 - COM-Template - Navigation mit 3 Ebenen','<?php\r\n// EXPLODE PATH\r\n$PATH = explode(\"|\",$this->getValue(\"path\").$this->getValue(\"article_id\").\"|\");\r\n// GET CURRENTS\r\n$path1 = $PATH[1];\r\n$path2 = $PATH[2];\r\n$path3 = $PATH[3];\r\n$catsArr = array();\r\n$nav = \'\';\r\n$c1 = 0;\r\n$lev1Cats = OOCategory::getRootCategories(true);\r\nforeach ($lev1Cats as $lev1) {\r\n	\r\n	if (rex_com_checkUserPerm($lev1->getValue(\"art_com_perm\"))) {\r\n				\r\n		if ($lev1->getId() == $path1)\r\n			$nav .= \'<li><a class=\"active\" href=\"\'.rex_getUrl($lev1->getId()).\'\"><span>\'.strtoupper($lev1->getName()).\'</span></a>\';\r\n		else\r\n			$nav .= \'<li><a href=\"\'.rex_getUrl($lev1->getId()).\'\"><span>\'.strtoupper($lev1->getName()).\'</span></a>\';\r\n		\r\n		\r\n		$lev2Cats = $lev1->getChildren(true);\r\n		\r\n		$nav2 = \'\';\r\n		if ($lev1->getId() == $path1 AND sizeof($lev2Cats) != 0) {\r\n			foreach ($lev2Cats as $lev2) {\r\n				if (rex_com_checkUserPerm($lev2->getValue(\"art_com_perm\"))) {\r\n					if ($lev2->getId() == $path2)\r\n						$nav2 .= \'<li><a class=\"active\" href=\"\'.rex_getUrl($lev2->getId()).\'\"><span>\'.strtoupper($lev2->getName()).\'</span></a>\';\r\n					else\r\n						$nav2 .= \'<li><a href=\"\'.rex_getUrl($lev2->getId()).\'\"><span>\'.strtoupper($lev2->getName()).\'</span></a>\';\r\n					\r\n					\r\n					$lev3Cats = $lev2->getChildren(true);\r\n			\r\n					$nav3 = \'\';\r\n					if ($lev2->getId() == $path2 AND sizeof($lev3Cats) != 0) {\r\n						foreach ($lev3Cats as $lev3) {\r\n							if (rex_com_checkUserPerm($lev3->getValue(\"art_com_perm\"))) {\r\n							\r\n								if ($lev3->getId() == $path3)\r\n									$nav3 .= \'<li><a class=\"active\" href=\"\'.rex_getUrl($lev3->getId()).\'\"><span>\'.strtoupper($lev3->getName()).\'</span></a></li>\';\r\n								else\r\n									$nav3 .= \'<li><a href=\"\'.rex_getUrl($lev3->getId()).\'\"><span>\'.strtoupper($lev3->getName()).\'</span></a></li>\';\r\n							}\r\n						}\r\n					}\r\n					if ($nav3 != \'\')\r\n						$nav2 .= \'<ul>\'.$nav3.\'</ul>\';\r\n					$nav2 .= \'</li>\';\r\n				}\r\n			}\r\n		}\r\n		if ($nav2 != \'\')\r\n			$nav .= \'<ul>\'.$nav2.\'</ul>\';\r\n		$nav .= \'</li>\';\r\n	}\r\n}\r\nprint \'<ul>\'.$nav.\'</ul>\';\r\n?>','0','admin','admin','1212572403','1212184378','a:1:{s:5:\"ctype\";a:0:{}}','0');
INSERT INTO `rex_template` VALUES ('13','','1016 - COM-Template - Navigation - Userbereiche 1 Ebene','<?php\r\n// Navigation für Usercategorien\r\n// BItte die Kategorie eintragen, die die Kategorien wie \"Mein Profil\", \"Meine Kontakte\" etc enthällt.\r\n$cat_id = REX_COM_USERCAT_ID;\r\n// EXPLODE PATH\r\n$PATH = explode(\"|\",$this->getValue(\"path\").$this->getValue(\"article_id\").\"|\");\r\n$cats = OOCategory::getChildrenById($cat_id, true);\r\n$catsArr = array();\r\nforeach ($cats as $lev1) {\r\n	if (rex_com_checkUserPerm($lev1->getValue(\"art_com_perm\")))		\r\n		$catsArr[$lev1->getId()][\'name\'] = $lev1->getName();\r\n}\r\n$c1 = 0;\r\n$nav = \'<ul>\';\r\nif (is_array($catsArr) AND sizeof($catsArr) != 0) \r\n{\r\n	foreach ($catsArr AS $lev1Id => $value) \r\n	{\r\n		$c1++;\r\n		$cl1 = \'\';\r\n		\r\n		if ($c1 == 1)\r\n			$cl1 = \' class=\"li-frst\"\';\r\n		elseif ($c1 == count($catsArr))\r\n			$cl1 = \' class=\"li-lst\"\';\r\n		\r\n		if (count($catsArr) == 1)\r\n			$cl1 = \' class=\"li-aln\"\';\r\n			\r\n		if ($lev1Id == $PATH[2])\r\n			$nav .= \'<li\'.$cl1.\'><a class=\"active\" href=\"\'.rex_getUrl($lev1Id).\'\"><span>\'.strtoupper($value[\'name\']).\'</span></a></li>\';\r\n		else\r\n			$nav .= \'<li\'.$cl1.\'><a href=\"\'.rex_getUrl($lev1Id).\'\"><span>\'.strtoupper($value[\'name\']).\'</span></a></li>\';\r\n		\r\n	}\r\n}\r\nif (isset($REX[\'COM_USER\']) && is_object($REX[\'COM_USER\'])) \r\n{\r\n	$nav .= \'<li class=\"li-aln\"><a href=\"\'.rex_getUrl(\'\', \'\', array(\'logout\'=>1)).\'\"><span>LOGOUT</span></a></li>\';\r\n}else \r\n{\r\n	$cl1_a =\'\';\r\n	if ($this->getValue(\'article_id\') == REX_COM_PAGE_LOGIN_ID)\r\n		$cl1_a = \' class=\"active\"\';\r\n	$nav .= \'<li class=\"li-aln\"><a\'.$cl1_a.\' href=\"\'.rex_getUrl(REX_COM_PAGE_LOGIN_ID).\'\"><span>LOGIN</span></a></li>\';\r\n}\r\n$nav .= \'</ul>\';\r\nprint $nav;\r\n?>','0','admin','admin','1212572404','1212185713','a:1:{s:5:\"ctype\";a:0:{}}','0');
INSERT INTO `rex_template` VALUES ('14','','1017 - COM-Template - Breadcrumb','<?php\r\n/*	Breadcrumb Navi	*************************************/\r\n$aid = $this->getValue(\'article_id\');\r\n$navi_brdcrmb = \'\';\r\n$article = OOArticle :: getArticleById($aid);\r\n$article_tree = $article->getParentTree();\r\nif (!$article->isStartpage()) {\r\n	$article_tree[] = $article;\r\n}\r\nif (is_array($article_tree)) {\r\n	$navi_brdcrmb .= \'<div class=\"com-path\"><p>Sie befinden sich hier: \';\r\n	\r\n	if ($aid != $REX[\'START_ARTICLE_ID\']) {\r\n		$navi_brdcrmb .= \'<a href=\"\'.rex_getUrl($REX[\'START_ARTICLE_ID\']).\'\">Home</a> &raquo; \';\r\n	}\r\n	\r\n	foreach ($article_tree as $article) {\r\n				\r\n		if ($article->hasTemplate() AND $article->isOnline()) {\r\n			if ($article->getId() == $aid) {\r\n				$navi_brdcrmb .= \'<span><a href=\"\'.$article->getUrl().\'\">\'.$article->_name.\'</a></span>\';\r\n			}\r\n			else {\r\n				$navi_brdcrmb .= \'<a href=\"\'.$article->getUrl().\'\">\'.$article->_name.\'</a> &raquo; \';\r\n			}\r\n		}\r\n	}\r\n	\r\n	if (substr($navi_brdcrmb, -9, 9) == \' &raquo; \')\r\n		$navi_brdcrmb = substr($navi_brdcrmb, 0, -9);\r\n	\r\n	$navi_brdcrmb .= \'</p></div>\';\r\n	\r\n	print $navi_brdcrmb;\r\n}\r\n?>','0','admin','admin','1212572405','1212170679','a:1:{s:5:\"ctype\";a:0:{}}','0');
