INSERT INTO `rex_article_slice` VALUES ('49','0','1','48','Es gibt ca. 600 gelistete Referenzen auf der Redaxo-Seite (Stand September 2007). Man kann jedoch davon ausgehen, daß die wirkliche Anzahl von Seiten deutlich größer ist.\r\n\r\n\"zurück\":?article_id=5','','','','','','','','','','','','','','','','','','','','','','','','','','','','','','','','','','','','','','','','','','','','','','','','','','','','','','','','','','','','','','19','3','1189527360','1189527463','rexdemo','rexdemo','');
