INSERT INTO rex_modultyp VALUES ('1','','bb - liste spielplan','0','<?php\r\n\r\n$qry = \'SELECT 	e.event_id,\r\n				e.event_time, \r\n				e.event_guest_event, \r\n				e.event_premiere,\r\n				p.program_name, \r\n				v.venue_name, \r\n				c.price_1, \r\n				c.price_2, \r\n				c.price_3, \r\n				c.price_4, \r\n				s.status_id, \r\n				s.status_name, \r\n				s.status_color\r\n		FROM	rex_43_events 			AS e,\r\n				rex_43_programs 		AS p,\r\n				rex_43_venues 			AS v,\r\n				rex_43_prices 			AS c,\r\n				rex_43_status 			AS s\r\n		WHERE 	e.event_program_id		= p.program_id\r\n		AND		e.event_venue_id		= v.venue_id\r\n		AND		e.event_price_id		= c.price_id\r\n		AND		e.event_status_id		= s.status_id\r\n		AND		e.event_time			>= \'.time().\'\r\n		AND		p.program_status		= 1\r\n		ORDER BY e.event_time ASC\';\r\n\r\n$sql = new sql();\r\n//$sql->debugsql = true;\r\n$sql->setQuery($qry);\r\n\r\n\r\n// The Months\r\n$month[\'1\'] = \'January\';\r\n$month[\'2\'] = \'February\';\r\n$month[\'3\'] = \'March\';\r\n$month[\'4\'] = \'April\';\r\n$month[\'5\'] = \'May\';\r\n$month[\'6\'] = \'June\';\r\n$month[\'7\'] = \'July\';\r\n$month[\'8\'] = \'August\';\r\n$month[\'9\'] = \'September\';\r\n$month[\'10\'] = \'October\';\r\n$month[\'11\'] = \'November\';\r\n$month[\'12\'] = \'December\';\r\n\r\n$month[\'1\'] = \'Januar\';\r\n$month[\'2\'] = \'Februar\';\r\n$month[\'3\'] = \'März\';\r\n$month[\'4\'] = \'April\';\r\n$month[\'5\'] = \'Mai\';\r\n$month[\'6\'] = \'Juni\';\r\n$month[\'7\'] = \'Juli\';\r\n$month[\'8\'] = \'August\';\r\n$month[\'9\'] = \'September\';\r\n$month[\'10\'] = \'Oktober\';\r\n$month[\'11\'] = \'November\';\r\n$month[\'12\'] = \'Dezember\';\r\n\r\n// The Weekdays\r\n$weekday[\'0\'] = \'Sunday\';\r\n$weekday[\'1\'] = \'Monday\';\r\n$weekday[\'2\'] = \'Tuesday\';\r\n$weekday[\'3\'] = \'Wednesday\';\r\n$weekday[\'4\'] = \'Thursday\';\r\n$weekday[\'5\'] = \'Friday\';\r\n$weekday[\'6\'] = \'Saturday\';\r\n\r\n// The Weekdays\r\n$weekday_short[\'Mon\'] = \'Mo\';\r\n$weekday_short[\'Tue\'] = \'Di\';\r\n$weekday_short[\'Wed\'] = \'Mi\';\r\n$weekday_short[\'Thu\'] = \'Do\';\r\n$weekday_short[\'Fri\'] = \'Fr\';\r\n$weekday_short[\'Sat\'] = \'Sa\';\r\n$weekday_short[\'Sun\'] = \'So\';\r\n\r\nfor ($i=1, $j=$sql->getRows(); $i<=$j; $i++) {\r\n	\r\n	$m = date(\'n\', $sql->getValue(\'event_time\'));\r\n	\r\n	$end = \'\';\r\n	if ($m != $m_old) {\r\n	\r\n		if ($i != 1) {\r\n			$cont .= \'</tbody></table>\';\r\n		}\r\n		$cont .= \'<h2>\'.$month[$m].\' \\\'\'.date(\'y\', $sql->getValue(\'event_time\')).\'</h2>\';\r\n		$cont .= \'<table>\r\n					<thead>\r\n					<tr>\r\n						<th class=\"c1\">Tag</th>\r\n						<th class=\"c2\">Datum</th>\r\n						<th class=\"c4\">Theaterstück / Vortrag</th>\r\n						<th class=\"c5\">Beginn</th>\r\n						<th class=\"c6\">Tickets</th>\r\n					</tr>\r\n					</thead>\r\n					\r\n					<tfoot>\r\n					<tr>\r\n						<td colspan=\"5\">Tickets: 0173 737 1759 oder per <a href=\"\'.rex_getUrl(4).\'\">Formular</a></td>\r\n					</tr>\r\n					</tfoot>\r\n					<tbody>\';\r\n	}\r\n	\r\n	//$cont .= \'<tr\'.$colored.\'>\r\n	$cont .= \'<tr style=\"background:\'.$sql->getValue(\'status_color\').\';\">\r\n				<td>\'.$weekday_short[date(\'D\', $sql->getValue(\'event_time\'))].\'</td>\r\n				<td>\'.date(\'d.m.y\', $sql->getValue(\'event_time\')).\'</td>\r\n				<td>\'.$sql->getValue(\'program_name\').\'</td>\r\n				<td>\'.date(\'H:i\', $sql->getValue(\'event_time\')).\'</td>\';\r\n	\r\n	$ticket = \'\';\r\n	if ($sql->getValue(\'status_id\') == 2) {\r\n		$ticket = \'<a href=\"\'.rex_getUrl(4, \'\', \'&eid=\'.$sql->getValue(\'event_id\')).\'\">\'.$sql->getValue(\'status_name\').\'</a>\';\r\n	}\r\n	else {\r\n		$ticket = $sql->getValue(\'status_name\');\r\n	}\r\n	\r\n	$cont .= \'	<td>\'.$ticket.\'</td>\r\n				</tr>\';\r\n					\r\n	$m_old = $m;\r\n	\r\n	$sql->next();\r\n}\r\n\r\nprint $cont.\'</tbody></table>\';\r\n\r\n?>','','','','0','0','','','','0','0');
INSERT INTO rex_modultyp VALUES ('2','','01 - Headline','0','<REX_VALUE[2]>REX_VALUE[1]</REX_VALUE[2]>','','&Uuml;berschrift:<br />\r\n<input type=\"text\" size=\"50\" name=\"VALUE[1]\" value=\"REX_VALUE[1]\" />\r\n<select name=\"VALUE[2]\" >\r\n<?php\r\nforeach (array(\"h1\",\"h2\",\"h3\",\"h4\",\"h5\",\"h6\") as $value) {\r\n	echo \'<option value=\"\'.$value.\'\" \';\r\n	\r\n	if ( \"REX_VALUE[2]\"==\"$value\" ) {\r\n		echo \'selected=\"selected\" \';\r\n	}\r\n	echo \'>\'.$value.\'</option>\';\r\n}\r\n?>\r\n</select>','','0','0','','','','0','0');
INSERT INTO rex_modultyp VALUES ('3','','bb - form - Ticket','0','<?php\r\n   \r\n// ============== Form config ==============\r\n\r\n	// Empfänger E-Mail\r\n	$empfaenger		= \"tbaddade@gmx.de\";\r\n	$email_title	= \"Ticketbestellung von www.bierhahntheater.de\";\r\n	$warning		= \' class=\"warning\"\';\r\n	\r\n	\r\n	// Daten für die Ausgabe der Fehlermeldung,\r\n	// Überschrift, Legend, Beschreibung, Feldnamen, \"Submit\"-Button\r\n	$page_info 		= \"\";\r\n	$form_h1		= \'E-MAIL\';\r\n	$hack_meldung 	= \"Leider konnten wir Ihre Anfrage nicht bearbeiten, weil das Formular nicht korrekt ausgefüllt ist. Bitte überprüfen Sie rotmarkierten Eingabefelder!\";\r\n   \r\n	//Formular\r\n	$form_legend1 	= \'Ihre Kontaktdaten\';\r\n	$form_legend2 	= \'Veranstaltung wählen\';\r\n	$form_legend3 	= \'Ihre Nachricht\';\r\n	$form_info 	 	= \"\";\r\n	$form_pflicht1	= \'Hinweis\';							// steht in <h2>\r\n	$form_pflicht2	= \'* Felder sind Pflichtfelder\';		// steht in <p>\r\n	\r\n	$form_name 	 	= \"Name *\";\r\n	$form_firstname = \"Vorname *\";\r\n	$form_telp		= \'Telefon\';\r\n	$form_email 	= \"E-Mail-Adresse\";\r\n	$form_street 	= \'Straße und HsNr. *\';\r\n	$form_zipcode 	= \'Postleitzahl *\';\r\n	$form_town	 	= \'Ort *\';\r\n	$form_land	 	= \'Land *\';\r\n	\r\n\r\n	$form_event 	= \'Veranstaltung\';\r\n	$form_price 	= \'Preisklasse\';\r\n	$form_tickets 	= \'Kartenanzahl\';\r\n	\r\n	$form_message 	= \"Ihre Nachricht *\";\r\n	$submit_value 	= \"Karten bestellen\";\r\n	$submit_titel 	= \"Zum Absenden bitte nur 1x klicken\";\r\n   \r\n	// Danke-Seite\r\n	$danke_title	= \'VIELEN DANK FÜR IHR INTERESSE\';\r\n	$danke_meldung 	= \"Wir werden Ihre Anfrage schnellstmöglich bearbeiten! Ihr Team vom Bierhahntheater.\";\r\n\r\n   \r\n// ============== Ende Form config ==============\r\n$eid = \'\';\r\n$step = \'\';\r\n$query = \'\';\r\nif (isset($_GET[\'eid\']) AND $_GET[\'eid\'] != \'\') {\r\n	$eid = $_GET[\'eid\'];\r\n	$query = \' AND event_id = \'.$eid;\r\n	$step = 2;\r\n}\r\nif (isset($_POST[\'event\']) AND $_POST[\'event\'] != \'\') {\r\n	$eid = $_POST[\'event\'];\r\n	$query = \' AND event_id = \'.$eid;\r\n}\r\nif (isset($_POST[\'step\']) AND $_POST[\'step\'] != \'\') {\r\n	$step = $_POST[\'step\'];\r\n}\r\n\r\n$qry = \'SELECT 	e.event_id,\r\n				e.event_time, \r\n				e.event_guest_event, \r\n				e.event_premiere,\r\n				p.program_name, \r\n				v.venue_name, \r\n				c.price_1, \r\n				c.price_2, \r\n				c.price_3, \r\n				c.price_4, \r\n				s.status_id, \r\n				s.status_name, \r\n				s.status_color\r\n		FROM	rex_43_events 			AS e,\r\n				rex_43_programs 		AS p,\r\n				rex_43_venues 			AS v,\r\n				rex_43_prices 			AS c,\r\n				rex_43_status 			AS s,\r\n				rex_43_artists_groups 	AS g\r\n		WHERE 	e.event_program_id		= p.program_id\r\n		AND		e.event_venue_id		= v.venue_id\r\n		AND		e.event_price_id		= c.price_id\r\n		AND		e.event_status_id		= s.status_id\r\n		AND		e.event_time			>= \'.time().\'\r\n		AND		p.program_artist_group_id = g.artist_group_id \r\n		AND		p.program_status		= 1\r\n		\'.$query.\'\r\n		ORDER BY e.event_time ASC\';\r\n\r\n$sql = new sql();\r\n$sql->setQuery($qry);\r\n\r\nif ($sql->getRows() > 1) {\r\n	$selout = \'<select id=\"levent\" name=\"event\" onchange=\"this.form.submit();\" tabindex=\"8\">\';\r\n	$selout .= \"\\n\".\'<option value=\"\">bitte Veranstaltung wählen!</option>\';\r\n	for ($i=1, $j=$sql->getRows(); $i<=$j; $i++) {\r\n		$selout .= \"\\n\".\'<option value=\"\'.$sql->getValue(\'event_id\').\'\">\'.date(\'d.m.y - H:i\', $sql->getValue(\'event_time\')).\' - \'.$sql->getValue(\'program_name\').\'</option>\';\r\n	\r\n		$sql->next();\r\n	}\r\n	$selout .= \'</select>\';\r\n	\r\n	$selout_p = \'<select id=\"lprice\" name=\"price\" tabindex=\"9\">\';\r\n	$selout_p .= \'<option>&nbsp;&nbsp;</option>\';\r\n	$selout_p .= \'</select>\';\r\n}\r\nelseif ($sql->getRows() == 1) {\r\n	$selout = \'<input type=\"hidden\" name=\"event\" value=\"\'.$eid.\'\" />\';\r\n	$selout .= \'<span>\'.date(\'d.m.y - H:i\', $sql->getValue(\'event_time\')).\' - \'.$sql->getValue(\'program_name\').\'</span>\';\r\n	\r\n	$selout_p = \'<select id=\"lprice\" name=\"price\">\';\r\n	$selected = \'\';\r\n	if ($sql->getValue(\'price_1\') != \'\') {\r\n		if ($_POST[\'price\'] == $sql->getValue(\'price_1\')) {\r\n			$selected = \' selected=\"selected\"\';\r\n		}\r\n		$selout_p .= \'<option\'.$selected.\' value=\"\'.$sql->getValue(\'price_1\').\'\">\'.$sql->getValue(\'price_1\').\' Euro</option>\';\r\n	}\r\n	if ($sql->getValue(\'price_2\') != \'\') {\r\n		if ($_POST[\'price\'] == $sql->getValue(\'price_2\')) {\r\n			$selected = \' selected=\"selected\"\';\r\n		}\r\n		$selout_p .= \'<option\'.$selected.\' value=\"\'.$sql->getValue(\'price_2\').\'\">\'.$sql->getValue(\'price_2\').\' Euro</option>\';\r\n	}\r\n	if ($sql->getValue(\'price_3\') != \'\') {\r\n		if ($_POST[\'price\'] == $sql->getValue(\'price_3\')) {\r\n			$selected = \' selected=\"selected\"\';\r\n		}\r\n		$selout_p .= \'<option\'.$selected.\' value=\"\'.$sql->getValue(\'price_3\').\'\">\'.$sql->getValue(\'price_3\').\' Euro</option>\';\r\n	}\r\n	if ($sql->getValue(\'price_4\') != \'\') {\r\n		if ($_POST[\'price\'] == $sql->getValue(\'price_4\')) {\r\n			$selected = \' selected=\"selected\"\';\r\n		}\r\n		$selout_p .= \'<option\'.$selected.\' value=\"\'.$sql->getValue(\'price_4\').\'\">\'.$sql->getValue(\'price_4\').\' Euro</option>\';\r\n	}\r\n	$selout_p .= \'</select>\';\r\n}\r\n\r\n\r\n\r\n// Daten aus Form config konvertieren\r\nif (!function_exists(clean_text)) {\r\n	function clean_text($value = \"\") {\r\n		$value = htmlspecialchars($value);\r\n		return $value;\r\n	}\r\n}\r\n	\r\n	$page_info 		= clean_text($page_info);\r\n	$hack_meldung 	= clean_text($hack_meldung);\r\n	$form_legend1 	= clean_text($form_legend1);\r\n	$form_legend2 	= clean_text($form_legend2);\r\n	$form_legend3 	= clean_text($form_legend3);\r\n	$form_pflicht1 	= clean_text($form_pflicht1);\r\n	$form_pflicht2 	= clean_text($form_pflicht2);\r\n	\r\n	$form_name		= clean_text($form_name);\r\n	$form_firstname	= clean_text($form_firstname);\r\n	$form_telp		= clean_text($form_telp);\r\n	$form_email 	= clean_text($form_email);\r\n	$form_street 	= clean_text($form_street);\r\n	$form_zipcode 	= clean_text($form_zipcode);\r\n	$form_town 		= clean_text($form_town);\r\n	$form_land 		= clean_text($form_land);\r\n	$form_message 	= clean_text($form_message);\r\n	\r\n	$form_event 	= clean_text($form_event);\r\n	$form_price 	= clean_text($form_price);\r\n	$form_tickets 	= clean_text($form_tickets);\r\n	\r\n	$form_message 	= clean_text($form_message);\r\n	\r\n	$submit_value 	= clean_text($submit_value);\r\n	$submit_titel 	= clean_text($submit_titel);\r\n	\r\n	$danke_title 	= clean_text($danke_title);\r\n	$danke_meldung 	= clean_text($danke_meldung);\r\n	\r\n   \r\n// ============== Formularüberprüfung ==============\r\n\r\nif (!function_exists(cleanup)) {\r\n	function cleanup ($string) {\r\n		// falls magic quotes on\r\n		if (get_magic_quotes_gpc()) {\r\n			$string = stripslashes($string);\r\n		}\r\n	\r\n		$string = addslashes($string);\r\n		$string = strip_tags($string);\r\n	\r\n		// \"--\",\"#\";\";\",\"`\",\"/\"   Ausfiltern von SQL-Statements\r\n		$string = str_replace(array(\"--\",\"- -\",\"#\",\";\",\"`\",\"/\",\"<\",\">\",\"{\",\"}\",\"[\",\"]\"),array(\"\",\"\",\"\",\"\",\"\",\"\",\"\",\"\",\"\",\"\",\"\",\"\"),$string);\r\n	\r\n		// - mit Leerzeichen dazwischen (SQL-Kommentare!)\r\n		$string = preg_replace(\'#-\\s+-#i\',\"\",$string);\r\n		return $string;\r\n	} // end function cleanup\r\n}\r\nif (!function_exists(checkName)) {\r\n	function checkName ($string) {\r\n		// $name am Anfang Buchstabe; keine Zahlen und Nicht-Wortzeichen; dann\r\n		// beliebig viele Buchstaben, Bindestriche, Leerzeichen und Punkte\r\n	\r\n		if (preg_match(\"/^[^\\W\\d_]([^\\W\\d_]|[\\-\\\'\\s\\.\\ä\\ö\\ü\\Ä\\Ö\\Ü\\ß])*$/\", stripslashes($string))) {\r\n			return true; // OK\r\n		}\r\n		else {\r\n			return false; // falsche Eingabe\r\n		}   \r\n	} // end function checkName\r\n}\r\n\r\nif (!function_exists(checkNumeric)) {\r\n	function checkNumeric ($string) {\r\n		// nur Zahlenr\r\n		if (preg_match(\"/^\\d+$/\", stripslashes($string))) {\r\n			return true; // OK\r\n		}\r\n		else {\r\n			return false; // falsche Eingabe\r\n		}   \r\n	} // end function checkNumeric\r\n}\r\n\r\nif (!function_exists(checkEmail)) {\r\n	function checkEmail ($string) {\r\n		// $email mindestens a@a.aa\r\n		if (ereg(\"^[_a-zA-Z0-9-]+(\\.[_a-zA-Z0-9-]+)*@([_a-zA-Z0-9-]+\\.)+([a-zA-Z]{2,4})$\", stripslashes($string))) {\r\n			return true; // OK\r\n		}\r\n		else {\r\n			return false; // falsche Eingabe\r\n		}   \r\n	} // end function checkEmail\r\n}\r\n\r\nif (!function_exists(checkMessage)) {\r\n	function checkMessage ($string) {\r\n		//\r\n  	  if (preg_match(\"/^[^\\d_]([^\\W]|[\\-\\\'\\s\\\"\\.\\!\\?\\;\\,\\:\\€\\%\\(\\)\\+\\=\\@\\*\\ä\\ö\\ü\\Ä\\Ö\\Ü\\ß])*$/\", stripslashes($string))) {\r\n			return true; // OK\r\n		}\r\n		else {\r\n			return false; // falsche Eingabe\r\n		}   \r\n	} // end function checkMessage\r\n}\r\n\r\n\r\n// Bool\'sche Variable zeigt an, ob\r\n// ein Hackingversuch vorliegt\r\n$hack = false;\r\n                       \r\n// Auslesen und Säubern der Daten aus $_POST\r\nif (sizeof($_POST) == 0) {\r\n	$name 		= \'\';\r\n	$email 		= \'\';\r\n	$message 	= \'\';\r\n	$submit 	= \'\';\r\n	$name		= \'\';\r\n	$firstname	= \'\';\r\n	$telp		= \'\';\r\n	$email 		= \'\';\r\n	$fstreet 	= \'\';\r\n	$zipcode 	= \'\';\r\n	$town 		= \'\';\r\n	$land 		= \'\';\r\n	$message 	= \'\';\r\n	\r\n	$event 		= \'\';\r\n	$price 		= \'\';\r\n	$tickets 	= \'\';\r\n	\r\n	$message 	= \'\';\r\n	\r\n	$fsubmit 	= \'\';\r\n}\r\nelse {	\r\n	\r\n	$name		= clean_text($_POST[\'name\']);\r\n	$firstname	= clean_text($_POST[\'firstname\']);\r\n	$telp		= clean_text($_POST[\'telp\']);\r\n	$email 		= clean_text($_POST[\'email\']);\r\n	$street 	= clean_text($_POST[\'street\']);\r\n	$zipcode 	= clean_text($_POST[\'zipcode\']);\r\n	$town 		= clean_text($_POST[\'town\']);\r\n	$land 		= clean_text($_POST[\'land\']);\r\n	$message 	= clean_text($_POST[\'message\']);\r\n	\r\n	$event 		= clean_text($_POST[\'event\']);\r\n	$price 		= clean_text($_POST[\'price\']);\r\n	$tickets 	= clean_text($_POST[\'tickets\']);\r\n	\r\n	$message 	= clean_text($_POST[\'message\']);\r\n	\r\n	$fsubmit 	= clean_text($_POST[\'fsubmit\']);\r\n	\r\n	\r\n	\r\n	// Verifizieren der Daten\r\n	\r\n	if (checkName($name) != 1 AND $step == 2) {\r\n		$hack 			= true; \r\n		$form_error[] 	= $form_name;\r\n		$class[\'name\'] 	= $warning;\r\n	}\r\n	\r\n	if (checkName($firstname) != 1 AND $step == 2) {\r\n		$hack 				= true; \r\n		$form_error[] 		= $form_firstname;\r\n		$class[\'firstname\'] = $warning;\r\n	}\r\n	\r\n	if (checkMessage($street) != 1 AND $step == 2) {\r\n		$hack 				= true; \r\n		$form_error[] 		= $form_street;\r\n		$class[\'street\'] 	= $warning;\r\n	}\r\n	\r\n	if (checkNumeric($zipcode) != 1 AND $step == 2) {\r\n		$hack 				= true; \r\n		$form_error[] 		= $form_zipcode;\r\n		$class[\'zipcode\'] 	= $warning;\r\n	}\r\n	\r\n	if (checkName($town) != 1 AND $step == 2) {\r\n		$hack 			= true; \r\n		$form_error[] 	= $form_town;\r\n		$class[\'town\'] 	= $warning;\r\n	}\r\n	/*\r\n	if (checkName($land) != 1 AND $step == 2) {\r\n		$hack 			= true; \r\n		$form_error[] 	= $form_land;\r\n		$class[\'land\'] 	= $warning;\r\n	}\r\n	*/\r\n	\r\n	if ($email != \'\' AND checkEmail($email) != 1 AND $step == 2) {\r\n		$hack 			= true; \r\n		$form_error[] 	= $form_email;\r\n		$class[\'email\'] = $warning;\r\n	}\r\n	\r\n	if (checkMessage($message) != 1 AND $step == 2) {\r\n		$hack 				= true; \r\n		$form_error[] 		= $form_message;\r\n		$class[\'message\'] 	= $warning;\r\n	}\r\n} // end else\r\n\r\n// ============== Ende Formularüberprüfung ==============\r\n\r\n\r\n// beim ersten Aufruf oder unkorrekten Eingaben Formular ausgeben\r\nif (($fsubmit == \'\') OR ($fsubmit == $submit_value AND ($step == \'\' OR $step == \'1\')) OR ($fsubmit == $submit_value AND $hack == true)) {\r\n	\r\n	\r\n	// Bei inkorrekten Eingaben eine Fehlermeldung ausgeben:\r\n	if ($hack) {\r\n		print \'<p>\'.$hack_meldung.\'</p>\';\r\n		\r\n	}	\r\n	else {\r\n		if ($page_info != \'\') {\r\n			print \'<p>\'.$page_info.\'</p>\';\r\n		}\r\n	}\r\n	\r\n	if ($step == \'\') {\r\n		$step = 1;\r\n	}\r\n	elseif ($step == \'1\') {\r\n		$step = 2;\r\n	}\r\n	\r\n	\r\n	print \'<h2>\'.$form_pflicht1.\'</h2>\r\n		<p>\'.$form_pflicht2.\'</p>\r\n		<div class=\"form-box\">\r\n			<form action=\"index.php?article_id=\'.$this->getValue(\"article_id\").\'\" method=\"post\">\r\n			<fieldset>\r\n				<legend>\'.$form_legend1.\'</legend>\r\n				<input type=\"hidden\" name=\"send\" value=\"1\" />\r\n				<input type=\"hidden\" name=\"step\" value=\"\'.$step.\'\" />\r\n				<input type=\"hidden\" name=\"article_id\" value=\"\'.$this->getValue(\"article_id\").\'\" />\r\n				\r\n				<p>\r\n				<label\'.$class[\'name\'].\' for=\"lname\">\'.$form_name.\'</label>\r\n				<input\'.$class[\'name\'].\' type=\"text\" id=\"lname\" name=\"name\" size=\"38\" title=\"\'.$form_name.\'\" value=\"\'.stripslashes($name).\'\" tabindex=\"1\" />\r\n				</p>\r\n				\r\n				<p>\r\n				<label\'.$class[\'firstname\'].\' for=\"lfirstname\">\'.$form_firstname.\'</label>\r\n				<input\'.$class[\'firstname\'].\' type=\"text\" id=\"lfirstname\" name=\"firstname\" size=\"38\" title=\"\'.$form_firstname.\'\" value=\"\'.stripslashes($firstname).\'\" tabindex=\"2\" />\r\n				</p>\r\n\r\n				\r\n				<p>\r\n				<label\'.$class[\'street\'].\' for=\"lstreet\">\'.$form_street.\'</label>\r\n				<input\'.$class[\'street\'].\' type=\"text\" id=\"lstreet\" name=\"street\" size=\"38\" title=\"\'.$form_street.\'\" value=\"\'.stripslashes($street).\'\" tabindex=\"3\" />\r\n				</p>\r\n\r\n				\r\n				<p>\r\n				<label\'.$class[\'zipcode\'].\' for=\"lzipcode\">\'.$form_zipcode.\'</label>\r\n				<input\'.$class[\'zipcode\'].\' type=\"text\" id=\"lzipcode\" name=\"zipcode\" size=\"38\" title=\"\'.$form_zipcode.\'\" value=\"\'.stripslashes($zipcode).\'\" tabindex=\"4\" />\r\n				</p>\r\n\r\n				\r\n				<p>\r\n				<label\'.$class[\'town\'].\' for=\"ltown\">\'.$form_town.\'</label>\r\n				<input\'.$class[\'town\'].\' type=\"text\" id=\"ltown\" name=\"town\" size=\"38\" title=\"\'.$form_town.\'\" value=\"\'.stripslashes($town).\'\" tabindex=\"5\" />\r\n				</p>\r\n				\r\n				\r\n				<p>\r\n				<label\'.$class[\'telp\'].\' for=\"ltelp\">\'.$form_telp.\'</label>\r\n				<input\'.$class[\'telp\'].\' type=\"text\" id=\"ltelp\" name=\"telp\" size=\"38\" title=\"\'.$form_telp.\'\" value=\"\'.stripslashes($telp).\'\" tabindex=\"6\" />\r\n				</p>\r\n				\r\n				<p>\r\n				<label\'.$class[\'email\'].\' for=\"lemail\">\'.$form_email.\'</label>\r\n				<input\'.$class[\'email\'].\' type=\"text\" id=\"lemail\" name=\"email\" size=\"38\" title=\"\'.$form_email.\'\" value=\"\'.stripslashes($email).\'\" tabindex=\"7\" />\r\n				</p>\r\n\r\n			</fieldset>\r\n				\r\n			<fieldset>\r\n				<legend>\'.$form_legend2.\'</legend>\r\n				\r\n				<p>\r\n				<label for=\"levent\">\'.$form_event.\'</label>\r\n				\'.$selout.\'\r\n				</p>\r\n				\r\n				<p>\r\n				<label for=\"lprice\">\'.$form_price.\'</label>\r\n				\'.$selout_p.\'\r\n				</p>\r\n				\r\n				<p>\r\n				<label for=\"lticket\">\'.$form_tickets.\'</label>\r\n				<select id=\"lticket\" name=\"tickets\" tabindex=\"10\">\r\n					<option value=\"1\">1</option>\r\n					<option value=\"2\">2</option>\r\n					<option value=\"3\">3</option>\r\n					<option value=\"4\">4</option>\r\n					<option value=\"5\">5</option>\r\n					<option value=\"6\">6</option>\r\n					<option value=\"7\">7</option>\r\n					<option value=\"8\">8</option>\r\n					<option value=\"9\">9</option>\r\n					<option value=\"10\">10</option>\r\n				</select>\r\n				</p>\r\n\r\n			</fieldset>\r\n				\r\n			<fieldset>\r\n				<legend>\'.$form_legend3.\'</legend>\r\n			\r\n				<p>\r\n				<label\'.$class[\'message\'].\' for=\"lmessage\">\'.$form_message.\'</label><br />\r\n				<textarea\'.$class[\'message\'].\' id=\"lmessage\" name=\"message\" title=\"\'.$form_message.\'\" cols=\"36\" rows=\"8\" tabindex=\"11\">\'.stripslashes($message).\'</textarea>\r\n				</p>\r\n\r\n				\r\n				<input class=\"fsubmit\" type=\"submit\" name=\"fsubmit\" value=\"\'.$submit_value.\'\" title=\"\'.$submit_titel.\'\" tabindex=\"12\" /><br />\r\n\r\n			</fieldset>\r\n			</form>\r\n			</div>\';\r\n}\r\n\r\nelseif ($fsubmit == $submit_value AND $step == 2 AND ($hack == false) AND $eid != \'\') {\r\n\r\n	$qry = \'SELECT 	e.event_id,\r\n					e.event_time, \r\n					p.program_name, \r\n					v.venue_name\r\n			FROM	rex_43_events 			AS e,\r\n					rex_43_programs 		AS p,\r\n					rex_43_venues 			AS v\r\n			WHERE 	e.event_program_id		= p.program_id\r\n			AND		e.event_venue_id		= v.venue_id\r\n			AND		e.event_id				= \'.$eid;\r\n\r\n	$sql = new sql();\r\n	//$sql->debugsql = true;\r\n	$sql->setQuery($qry);\r\n\r\n	if ($sql->getRows() == 1) {\r\n		//email abschicken\r\n	\r\n		$mail = new PHPMailer();\r\n		$mail->AddAddress($empfaenger);\r\n		$mail->From 	= $email;\r\n		$mail->Subject 	= $email_title;\r\n	\r\n		$mailbody = \"\\n\";\r\n		$mailbody .= \'Nachname: \'	.\"\\t\"	.$name.\"\\n\";\r\n		$mailbody .= \'Vorname: \'	.\"\\t\"	.$firstname.\"\\n\";\r\n		$mailbody .= \'Strasse: \'	.\"\\t\"	.$street.\"\\n\";\r\n		$mailbody .= \'PLZ / Ort: \'	.\"\\t\"	.$zipcode.\' / \'.$town.\"\\n\\n\";\r\n	\r\n		$mailbody .= \'Telefon: \'	.\"\\t\"	.$telp.\"\\n\";\r\n		$mailbody .= \'E-Mail: \'		.\"\\t\"	.$email.\"\\n\\n\";\r\n	\r\n		$mailbody .= \'Veranstalung: \'.\"\\t\"	.date(\'d.m.y - H:i\', $sql->getValue(\'event_time\')).\' - \'.$sql->getValue(\'program_name\').\"\\n\";\r\n		$mailbody .= \'Preisklasse: \'.\"\\t\"	.$price.\" Euro\\n\\n\";\r\n		$mailbody .= \'Kartenanzahl:\'.\"\\t\"	.$tickets.\"\\n\\n\";\r\n	\r\n		$mailbody .= \'Nachricht: \'	.\"\\n\"	.$message;\r\n	\r\n		$mail->Body = $mailbody;\r\n		$mail->Send();\r\n	\r\n		print \'<h2>\'.$danke_title.\'</h2><p>\'.$danke_meldung.\'</p>\';\r\n	}\r\n} \r\n/*\r\n	print \'<pre>\';\r\n	print_r($_POST);\r\n	print \'</pre>\';\r\n\r\n	print \'Step\'.$step.\'<br>\';\r\n	print \'Eid\'.$eid.\'<br>\';\r\n	if (!$hack) $hck = 0;\r\n	else $hck = 1;\r\n	print \'Hack\'.$hck.\'<br>\';\r\n	\r\n	print \'fs\'.$fsubmit.\'<br>\';\r\n	print \'sv\'.$submit_value.\'<br>\';\r\n*/\r\n	\r\n?>\r\n','','','','0','0','','','','0','0');
INSERT INTO rex_template VALUES ('1','','default','<?php\r\n	include $REX[\'INCLUDE_PATH\'].\"/generated/templates/2.template\";\r\n	\r\n	$aid = $this->getValue(\"article_id\");\r\n	\r\n	\r\n	$article = OOArticle::getArticleById(1, $REX[\'CUR_CLANG\']);\r\n	$articleK = $article->getValue(\"_keywords\");\r\n	$articleD = $article->getDescription();\r\n	\r\n	\r\n	if($this->getValue(\"description\") != \"\") {\r\n		$meta_beschreibung = htmlspecialchars($this->getValue(\"description\"));\r\n	}\r\n	else {\r\n		$meta_beschreibung = htmlspecialchars($articleD);\r\n	}\r\n	\r\n	if($this->getValue(\"keywords\")!= \"\") {\r\n		$meta_suchbegriffe = htmlspecialchars($this->getValue(\"keywords\"));\r\n	} \r\n	else {\r\n		$meta_suchbegriffe = htmlspecialchars($articleK);\r\n	}\r\n	\r\n	$body = \'\';\r\n	$body = \' id=\"page-\'.$aid.\'\"\';\r\n	\r\n?>\r\n<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Strict//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-strict.dtd\">\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\" xml:lang=\"de\" lang=\"de\">\r\n<head>\r\n<meta http-equiv=\"content-type\" content=\"text/html; charset=iso-8859-1\" />\r\n<title><? print $REX[\'SERVERNAME\'].\' | \'.$this->getValue(\"name\"); ?></title>\r\n<meta name=\"keywords\" content=\"<?php print $meta_suchbegriffe; ?>\" />\r\n<meta name=\"description\" content=\"<?php print $meta_beschreibung; ?>\" />\r\n<link rel=\"stylesheet\" type=\"text/css\" href=\"<? echo $REX[\'HTDOCS_PATH\'] ?>files/main.css\" media=\"screen\" />\r\n</head>\r\n<body<?php print $body; ?>>\r\n<div id=\"website\">\r\n\r\n	<!-- start HEADER -->\r\n	<div id=\"header\">\r\n		<div id=\"logo\">\r\n			<a href=\"/index.php\" title=\"Zur&uuml;ck zur Startseite\">Kulturhaus Bischofswerda</a>\r\n		</div>\r\n		\r\n		<div id=\"navi\">\r\n			<?php print $nav; ?>\r\n		</div>\r\n	</div>\r\n	<!-- Ende header -->\r\n\r\n	<!-- start Image -->\r\n	<div id=\"image\">\r\n		<ul>\r\n			<li><a href=\"/index.php\">Startseite</a></li>\r\n			<li><a href=\"#\">Kontakt</a></li>\r\n			<li><a href=\"#\">Impressum</a></li>\r\n		</ul>\r\n	<div id=\"image2\">\r\n		<?php \r\n			$file = $this->getValue(\'file\');\r\n			\r\n			if ($file == \'\') {\r\n				$art = OOArticle::getArticleById($REX[\'START_ARTICLE_ID\']);\r\n				$file = $art->getFile();\r\n			}\r\n			\r\n			print \'<p><img src=\"files/\'.$file.\'\" /></p>\';\r\n		?>\r\n	</div></div>\r\n	<!-- Ende Image -->\r\n	\r\n	<div id=\"wrapper\">\r\n	\r\n		<!-- start #content -->\r\n		<div id=\"content\">\r\n		<div id=\"content2\">\r\n			<?php print $this->getArticle(0); ?>\r\n		</div></div>\r\n		<!-- Ende content -->\r\n\r\n		<!-- start #rFrame -->\r\n		<div id=\"rFrame\">\r\n			<p><img src=\"files/tmp_sidebar.jpg\" height=\"103\" width=\"290\" /></p>\r\n			<p><img src=\"files/tmp_sidebar.jpg\" height=\"103\" width=\"290\" /></p>\r\n			<p><img src=\"files/tmp_sidebar.jpg\" height=\"103\" width=\"290\" /></p>\r\n			<p><img src=\"files/tmp_sidebar.jpg\" height=\"103\" width=\"290\" /></p>\r\n		</div>\r\n		\r\n		<div class=\"clearer\"> </div>\r\n	</div>\r\n	<!-- Ende rFrame -->\r\n	\r\n	<!-- start #footer -->\r\n	<div id=\"footer\">\r\n		<p>&copy; <?php echo date(\"Y\"); ?>: kulturhaus-bischofswerda.de</p>\r\n	</div>\r\n	<!-- Ende footer -->\r\n\r\n	<div class=\"clearer\"> </div>\r\n\r\n\r\n</div> <!-- end #website -->\r\n\r\n\r\n\r\n\r\n</body>\r\n\r\n</html>','','1','20060621104809','thomas','thomas','1150131999','1150879689');
INSERT INTO rex_template VALUES ('2','','navigation','<?php\r\n// EXPLODE PATH\r\n$PATH = explode(\"|\",$this->getValue(\"path\").$this->getValue(\"article_id\").\"|\");\r\n\r\n// GET CURRENTS\r\nif (isset($PATH[1])) $path1 = $PATH[1];\r\nif (isset($PATH[2])) $path2 = $PATH[2];\r\nif (isset($PATH[3])) $path3 = $PATH[3];\r\n\r\n\r\n\r\n//$couM = 1;\r\n\r\nforeach (OOCategory::getRootCategories() as $lev1) {\r\n\r\n	\r\n	if ($lev1->isOnline()) {\r\n		\r\n		$class1 = \'\';\r\n		if ($lev1->getId() == $path1) {\r\n			$class1 = \' class=\"current\"\';\r\n		}\r\n		\r\n		$nav .= \'<li id=\"nav\'.$lev1->getId().\'\"><a\'.$class1.\' href=\"\'.$lev1->getUrl().\'\">\'.$lev1->getName().\'</a>\';\r\n		\r\n		\r\n		if (sizeof($lev1->getChildren(true)) != 0 AND $lev1->getId() == $path1) {\r\n			\r\n			$nav2 .= \'<ul>\'.\"\\n\";\r\n			foreach ($lev1->getChildren(true) as $lev2) {\r\n				\r\n				$class2 = \'\';\r\n				if ($lev2->getId() == $path2) {\r\n					$class2 = \' class=\"current\"\';\r\n				}\r\n					\r\n				$nav2 .= \'<li><a\'.$class2.\' href=\"\'.$lev2->getUrl().\'\">\'.$lev2->getName().\'</a></li>\'.\"\\n\";\r\n					\r\n			}\r\n			$nav2 .= \'</ul>\'.\"\\n\";\r\n		}\r\n		$nav .= \'</li>\'.\"\\n\";\r\n	}\r\n}\r\n\r\nif ($nav != \'\') {\r\n	$nav = \'<ul>\'.$nav.\'</ul>\';\r\n}\r\n\r\nif ($nav2 != \'\') {\r\n	$nav2 = $nav2;\r\n}\r\n?>','','0','20060621085322','thomas','thomas','1150284249','1150872802');
