INSERT INTO `rex_module` VALUES ('2','01 - Text und/oder Bild [textile]','0','<?php\r\n\r\nif(OOAddon::isAvailable(\'textile\'))\r\n{\r\n\r\n?>\r\n\r\n<div class=\"team\">\r\n<?php\r\n\r\n//	Ausrichtung des Bildes \r\nif (\"REX_VALUE[9]\" == \"l\") $float = \"floatLeft\";\r\nif (\"REX_VALUE[9]\" == \"r\") $float = \"floatRight\";\r\n\r\n\r\n//	Wenn Bild eingefuegt wurde, Code schreiben \r\n$file = \"\";\r\nif (\"REX_FILE[1]\" != \"\") $file = \'<div class=\"\'.$float.\'\"><img src=\"\'.$REX[\'HTDOCS_PATH\'].\'files/REX_FILE[1]\" title=\"\'.\"REX_VALUE[2]\".\'\" alt=\"\'.\"REX_VALUE[2]\".\'\" /></div>\';\r\n\r\n\r\n  $text = \'\';\r\n  if(REX_IS_VALUE[1])\r\n  {\r\n// Diese 3 Zeilen dürfen keine führenden Leerzeichen besitzen!\r\n$text =<<<EOD\r\nREX_HTML_VALUE[1]\r\nEOD;\r\n\r\n    $text = rex_a79_textile($text);\r\n  } \r\n\r\n $text = str_replace(\"###\",\"&#x20;\",$text);\r\n\r\n\r\nprint $file.$text;\r\n\r\n}\r\nelse\r\n{\r\n  echo rex_warning(\'Dieses Modul benötigt das \"textile\" Addon!\');\r\n}\r\n\r\n?>\r\n</div>','<?php\r\n\r\n//---MODULE BY----------------------\r\n//-- blumbeet - web.studio\r\n//-- Thomas Blum\r\n//-- www.blumbeet.com\r\n//----------------------------------\r\n\r\nif(OOAddon::isAvailable(\'textile\'))\r\n{\r\n?>\r\n\r\n<strong>Fliesstext</strong>:<br />\r\n<textarea name=\"VALUE[1]\" cols=\"80\" rows=\"10\" class=\"inp100\">REX_HTML_VALUE[1]</textarea>\r\n<br /><br />\r\n\r\n\r\n<strong>Artikelfoto</strong>:<br />\r\nREX_MEDIA_BUTTON[1]\r\n<?\r\nif (\"REX_FILE[1]\" != \"\") {\r\n        echo \"<br /><strong>Vorschau</strong>:<br />\";\r\n	echo \"<img src=\".$REX[\'HTDOCS_PATH\'].\"/files/REX_FILE[1]><br />\";\r\n}\r\n\r\n?>\r\n\r\n<br />\r\n<strong>Title des Fotos</strong>:<br />\r\n<input type=\"text\" name=\"VALUE[2]\" value=\"REX_VALUE[2]\" size=\"80\" class=\"inp100\" />\r\n<br /><br />\r\n\r\n<strong>Ausrichtung des Artikelfotos</strong>:<br />\r\n<select name=\"VALUE[9]\" class=\"inp100\">\r\n	<option value=\'l\' <?php if (\"REX_VALUE[9]\" == \'l\') echo \'selected\'; ?>>links vom Text</option>\r\n</select><br />\r\n<br />\r\n<br />\r\n<?php\r\n  rex_a79_help_overview(); \r\n}\r\nelse\r\n{\r\n  echo rex_warning(\'Dieses Modul benötigt das \"textile\" Addon!\');\r\n}\r\n\r\n?>','admin','admin','1181731594','1192182221','','0');
INSERT INTO `rex_module` VALUES ('3','01 - Text [textile]','0','<?php\r\n\r\nif(OOAddon::isAvailable(\'textile\'))\r\n{\r\n\r\n// Fliesstext \r\n  $text = \'\';\r\n  if(REX_IS_VALUE[1])\r\n  {\r\n\r\n// Diese 3 Zeilen dürfen keine führenden Leerzeichen besitzen!\r\n$text =<<<EOD\r\nREX_HTML_VALUE[1]\r\nEOD;\r\n\r\n    $text = rex_a79_textile($text);\r\n\r\n} \r\n\r\n$text = str_replace(\"###\",\"&#x20;\",$text);\r\n\r\nprint \'<div class=\"txt-img\">\'. $text. \'</div>\';\r\n\r\n}else\r\n{\r\n  echo rex_warning(\'Dieses Modul benötigt das \"textile\" Addon!\');\r\n}\r\n\r\n?>','<?php\r\n\r\n//---MODULE BY----------------------\r\n//-- blumbeet - web.studio\r\n//-- Thomas Blum\r\n//-- www.blumbeet.com\r\n//----------------------------------\r\n\r\nif(OOAddon::isAvailable(\'textile\'))\r\n{\r\n?>\r\n\r\n<strong>Fliesstext</strong>:<br />\r\n<textarea name=\"VALUE[1]\" cols=\"80\" rows=\"10\" class=\"inp100\">REX_HTML_VALUE[1]</textarea>\r\n<br />\r\n\r\n<?php\r\n\r\nrex_a79_help_overview(); \r\n\r\n}else\r\n{\r\n  echo rex_warning(\'Dieses Modul benötigt das \"textile\" Addon!\');\r\n}\r\n\r\n?>','admin','admin','1181731625','1192181901','','0');
INSERT INTO `rex_module` VALUES ('5','05 - Artikelliste','0','<?php\r\n\r\n$cat = OOCategory::getCategoryById($this->getValue(\"category_id\"));\r\n$article = $cat->getArticles();\r\n\r\nif (is_array($article)) \r\n{\r\n  foreach ($article as $var) \r\n  {\r\n    $articleId = $var->getId();\r\n    $articleName = $var->getName();\r\n    $articleDescription = $var->getDescription();\r\n    if ($var->getValue(\'_startpage\') != 1) \r\n    {\r\n      echo \'<a href=\"\'.rex_getUrl($articleId).\'\" class=\"faq\">\'.$articleName.\'</a><br />\';\r\n    }\r\n  }\r\n}\r\n\r\n?>','','admin','admin','1181731691','1192227657','','0');
INSERT INTO `rex_module` VALUES ('6','05 - Kategorienliste','0','<?php\r\n\r\n$cat = OOCategory :: getCategoryById($this->getValue(category_id));\r\n$cats = $cat->getChildren();\r\n\r\n$itemsPerSide = \"REX_VALUE[1]\";\r\n$wordsPerArticle = \"REX_VALUE[2]\";\r\n\r\nif (is_array($cats))\r\n{\r\n  foreach ($cats as $cat)\r\n  {\r\n    $i += 1;\r\n    if ($i <= $itemsPerSide)\r\n    {\r\n      if ($cat->isOnline())\r\n      {\r\n\r\n        $catId = $cat->getId();\r\n        $catName = $cat->getName();\r\n        $article = $cat->getArticles();\r\n\r\n        if (is_array($article))\r\n        {\r\n          foreach ($article as $var)\r\n          {\r\n            $articleId = $var->getId();\r\n            $articleName = $var->getName();\r\n            $art = new rex_article($articleId);\r\n            $articleContent = $art->getArticle();\r\n\r\n            $articleContent = trim($articleContent);\r\n            $articleContent = str_replace(\'</p>\', \' </p>\', $articleContent);\r\n            $articleContent = str_replace(\'<br />\', \' <br />\', $articleContent);\r\n\r\n            $articlePPath = $REX[\'MEDIAFOLDER\'] . \'files/\' . $var->getValue(\'file\');\r\n\r\n            $output = \'\';\r\n            $words = explode(\' \', $articleContent);\r\n            $wordsCount = count($words);\r\n\r\n            if ($wordsCount < $wordsPerArticle)\r\n              $wEnd = $wordsCount;\r\n            else\r\n              $wEnd = $wordsPerArticle;\r\n\r\n            for ($w = 0; $w < $wEnd; $w++)\r\n            {\r\n              $output .= $words[$w] . \' \';\r\n            }\r\n\r\n            $output = trim($output);\r\n\r\n            $isCloseParagraph = substr($output, -4);\r\n            $link = \'<a href=\"\' . rex_getUrl($articleId) . \'\" class=\"more\"> ...mehr</a>\';\r\n            $newString = $link . \'</p>\';\r\n\r\n            if ($isCloseParagraph == \'</p>\')\r\n            {\r\n              $output = substr_replace($output, $newString, -4);\r\n            }\r\n            else\r\n            {\r\n              $output .= $newString;\r\n            }\r\n\r\n            // print \'<h2>\'.$articleName.\'</h2>\';\r\n            print \'<div class=\"txt-img\">\' . $output . \'</div>\';\r\n\r\n          }\r\n        }\r\n      }\r\n    }\r\n  }\r\n}\r\n?>','<?php\r\n\r\n//---MODULE BY----------------------\r\n//-- Wegener IT\r\n//-- Mattias Beckmann\r\n//-- www.wegener-it.de\r\n//----------------------------------\r\n\r\n?>\r\n\r\n<strong>Anzahl der Artikel pro Seite</strong><br />\r\n<input name=\"VALUE[1]\" value=\"REX_VALUE[1]\" class=\"inp100\" />\r\n\r\n<br /><br />\r\n<strong>Anzahl der Wörter pro Artikel</strong><br />\r\n<input name=\"VALUE[2]\" value=\"REX_VALUE[2]\" class=\"inp100\" />\r\n\r\n<br />','admin','admin','1181731741','1192182044','','0');
INSERT INTO `rex_module` VALUES ('8','04 - Artikelweiterleitung','0','<?php\r\n\r\nif($REX[\'REDAXO\']!=1 && REX_ARTICLE_ID != REX_LINK_ID[1])\r\n{\r\n  if ( REX_LINK_ID[1] != 0) \r\n  {\r\n   rex_redirect(REX_LINK_ID[1], $REX[\'CUR_CLANG\']);\r\n  }\r\n}else\r\n{\r\n  echo \"Weiterleitung zu <a href=\'index.php?page=content&article_id=REX_LINK_ID[1]&mode=edit\'>Artikel           REX_LINK[1]</a>\";\r\n}\r\n\r\n?>','Artikel, zu dem Weitergeleitet werden soll:<br /><br />\r\nREX_LINK_BUTTON[1]','admin','admin','1181731807','1192181933','','0');
INSERT INTO `rex_module` VALUES ('9','03 - Bildgalerie','0','<?php\r\n\r\nif (!isset($REX[\'MODULE_BILDGALERIE_ID\'])) $REX[\'MODULE_BILDGALERIE_ID\'] = 0;\r\nelse $REX[\'MODULE_BILDGALERIE_ID\']++;\r\n\r\nif ($REX[\'MODULE_BILDGALERIE_ID\']==0)\r\n{\r\n?>\r\n<script type=\"text/javascript\">\r\nvar GB_ROOT_DIR = \"files/\";\r\n</script>\r\n<script type=\"text/javascript\" src=\"files/AJS.js\"></script>\r\n<script type=\"text/javascript\" src=\"files/AJS_fx.js\"></script>\r\n<script type=\"text/javascript\" src=\"files/gb_scripts.js\"></script>\r\n<link href=\"files/gb_styles.css\" rel=\"stylesheet\" type=\"text/css\" />\r\n\r\n<?php\r\n}\r\n?>\r\n<div class=\"galerie\"><?php\r\n\r\n$pics_string = \"REX_MEDIALIST[1]\";\r\n$pics = explode(\',\',$pics_string);\r\n\r\nforeach($pics as $pic)\r\n{\r\n  echo \'<div class=\"image\">\';\r\n\r\n  $title = \'\';\r\n  if ($file = OOMedia::getMediaByFileName($pic)) $title = $file->getTitle();\r\n\r\n  echo \'<a href=\"\'.$REX[\'HTDOCS_PATH\'].\'/files/\'.$pic.\'\" rel=\"gb_imageset[galerie\'.$REX[\'MODULE_BILDGALERIE_ID\'].\']\"><img src=\"\'.$REX[\'HTDOCS_PATH\'].\'/index.php?rex_resize=250a__\'.$pic.\'\" title=\"\'.$title.\'\" alt=\"\'.$title.\'\" /></a>\';\r\n\r\n  echo \'<p>\'.$title.\'</p>\';\r\n  echo \'</div>\';\r\n}\r\n\r\n?></div>','Bitte Bilder auswählen:\r\n<br />REX_MEDIALIST_BUTTON[1]\r\n<br /><br />','admin','admin','1192188185','1192276336','','0');
INSERT INTO `rex_modultyp` VALUES ('1','01 - Text und/oder Bild [textile]','0','<?\r\n//	Ausrichtung des Bildes \r\nif (\"REX_VALUE[9]\" == \"l\") $float = \"flLeft\";\r\nif (\"REX_VALUE[9]\" == \"r\") $float = \"flRight\";\r\n\r\n\r\n//	Wenn Bild eingefuegt wurde, Code schreiben \r\n$file = \"\";\r\nif (\"REX_MEDIA[1]\" != \"\") $file = \'<div class=\"image \'.$float.\'\"><img src=\"\'.$REX[\'HTDOCS_PATH\'].\'files/REX_MEDIA[1]\" title=\"\'.\"REX_VALUE[2]\".\'\" alt=\"\'.\"REX_VALUE[2]\".\'\" /><p>\'.\"REX_VALUE[3]\".\'</p></div>\';\r\n\r\n\r\n//	Fliesstext \r\n$input =<<< EOT\r\nREX_HTML_VALUE[1]\r\nEOT;\r\n$textile = new Textile; \r\n$text = \"\";\r\nif (\"REX_VALUE[1]\" != \"\") $text = $textile->TextileThis($input);\r\n\r\n\r\n$text = str_replace(\"###\",\"&#x20;\",$text);\r\n\r\n\r\nprint $file.$text;\r\n\r\n?>','<?php\r\n//---MODULE BY----------------------\r\n//-- blumbeet - web.studio\r\n//-- Thomas Blum\r\n//-- www.blumbeet.com\r\n//----------------------------------\r\n?>\r\n\r\n<strong>Fliesstext</strong>:<br />\r\n<textarea name=\"VALUE[1]\" cols=\"80\" rows=\"10\" class=\"inp100\">REX_HTML_VALUE[1]</textarea>\r\n<br /><br />\r\n\r\n\r\n<strong>Artikelfoto</strong>:<br />\r\nREX_MEDIA_BUTTON[1]\r\n<?\r\nif (\"REX_FILE[1]\" != \"\") {\r\n        echo \"<br/><strong>Vorschau</strong>:<br/>\";\r\n	echo \"<img src=\".$REX[HTDOCS_PATH].\"/files/REX_FILE[1]><br />\";\r\n}\r\n\r\n?>\r\n\r\n<br/>\r\n<strong>Title des Fotos</strong>:<br />\r\n<input type=\"text\" name=\"VALUE[2]\" value=\"REX_VALUE[2]\" size=\"80\" class=\"inp100\" />\r\n<br /><br />\r\n\r\n<strong>Bildunterschrift</strong>:<br />\r\n<input type=\"text\" name=\"VALUE[3]\" value=\"REX_VALUE[3]\" size=\"80\" class=\"inp100\" />\r\n<br /><br />\r\n\r\n<strong>Ausrichtung des Artikelfotos</strong>:<br />\r\n<select name=\"VALUE[9]\" class=\"inp100\">\r\n	<option value=\'l\' <? if (\"REX_VALUE[9]\" == \'l\') echo \'selected\'; ?>>links vom Text</option>\r\n	<option value=\'r\' <? if (\"REX_VALUE[9]\" == \'r\') echo \'selected\'; ?>>rechts vom Text</option>\r\n</select><br />\r\n<br />\r\n<br />\r\n<strong>Anleitung / Hinweise</strong>:\r\n<table class=\"warning\">\r\n<tr>\r\n	<th style=\"width:200px;\"><strong>Beschreibung</strong></th>\r\n	<th><strong>Eingabe</strong></th>\r\n</tr>\r\n<tr>\r\n	<td><h1>Überschrift</h1></td>\r\n	<td>h1. Überschrift (Leerzeile vor und nach der Eingabe)</td>\r\n</tr>\r\n<tr>\r\n	<td><h2>Unter-überschrift</h2></td>\r\n	<td>h2. Überschrift (Leerzeile vor und nach der Eingabe)</td>\r\n</tr>\r\n<tr>\r\n	<td><strong>fetter Text</strong></td>\r\n	<td>*fetter Text*</td>\r\n</tr>\r\n<tr>\r\n	<td><i>kursiver Text</i></td>\r\n	<td>__kursiver Text__</td>\r\n</tr>\r\n<tr>\r\n	<td><del>gestrichener Text</del></td>\r\n	<td>-gestrichener Text-</td>\r\n</tr>\r\n<tr>\r\n	<td>geordnete Liste mit Zahlen</td>\r\n	<td># Listenpunkt</td>\r\n</tr>\r\n<tr>\r\n	<td>ungeordnete Liste mit Zeichen</td>\r\n	<td>* Listenpunkt</td>\r\n</tr>\r\n<tr>\r\n	<td>Link (intern)</td>\r\n	<td>\"Hier gehts zum Impressum\":redaxo://5</td>\r\n</tr>\r\n<tr>\r\n	<td>Link (extern)</td>\r\n	<td>\"Hier geht es zu unserem Partner\":http://pergopa.de</td>\r\n</tr>\r\n</table>','','admin','0','1168628226','');
INSERT INTO `rex_modultyp` VALUES ('2','01 - Headline','0','<REX_VALUE[2]>REX_VALUE[1]</REX_VALUE[2]>','&Uuml;berschrift:<br />\r\n<input type=\"text\" size=\"50\" name=\"VALUE[1]\" value=\"REX_VALUE[1]\" />\r\n<select name=\"VALUE[2]\" >\r\n<?php\r\nforeach (array(\"h1\",\"h2\",\"h3\",\"h4\",\"h5\",\"h6\") as $value) {\r\n	echo \'<option value=\"\'.$value.\'\" \';\r\n	\r\n	if ( \"REX_VALUE[2]\"==\"$value\" ) {\r\n		echo \'selected=\"selected\" \\\';\r\n	}\r\n	echo \'>\'.$value.\'</option>\';\r\n}\r\n?>\r\n</select>','','admin','0','1183727415','');
INSERT INTO `rex_modultyp` VALUES ('3','02 - Bild','0','<?\r\n\r\nif (\"REX_FILE[1]\" != \"\") {\r\n	print \'<img src=\"\'.$REX[\'HTDOCS_PATH\'].\'files/\'.\"REX_FILE[1]\".\'\" alt=\"\'.\"REX_VALUE[1]\".\'\" title=\"\'.\"REX_VALUE[1]\".\'\" />\';\r\n}\r\n?>','Titel des Bildes:<br /> \r\n<input type=text size=80 name=VALUE[1] value=\"REX_VALUE[1]\"><br /><br />\r\n\r\nREX_MEDIA_BUTTON[1]\r\n<?\r\n\r\nif (\"REX_FILE[1]\" != \"\") {\r\n	print \'<img src=\"\'.$REX[\'HTDOCS_PATH\'].\'files/\'.\"REX_FILE[1]\".\'\" alt=\"\'.\"REX_VALUE[1]\".\'\" title=\"\'.\"REX_VALUE[1]\".\'\" />\';\r\n}\r\n?>','','','0','0','');
INSERT INTO `rex_modultyp` VALUES ('4','03 - Externer Link','0','<?php\r\nif (\"REX_VALUE[1]\" == \"\") {\r\n	print \'<p>REX_VALUE[2]</p>\';\r\n}\r\nelse {\r\n	print \'<p><a href=\"REX_VALUE[1]\" title=\"REX_VALUE[2]\" alt=\"REX_VALUE[2]\">REX_VALUE[2]</a></p>\';\r\n}\r\n\r\n?>','vollständige Url:<br /> \r\n<input type=text size=80 name=VALUE[1] value=\"REX_VALUE[1]\"><br /><br />\r\n\r\nLink Text der zu sehen ist:<br /> \r\n<input type=text size=80 name=VALUE[2] value=\"REX_VALUE[2]\"><br /><br />','','admin','0','1181221841','');
INSERT INTO `rex_modultyp` VALUES ('5','03 - Interner Link','0','<?php\r\n\r\nif (\"REX_LINK[1]\") {\r\n	print \'<p><a href=\"\'.\"REX_LINK[1]\".\'\" title=\"\'.\"REX_VALUE[1]\".\'\" alt=\"\'.\"REX_VALUE[1]\".\'\">\'.\"REX_VALUE[1]\".\'</a></p>\';\r\n}\r\n\r\n?>','interner Link:<br /> \r\nREX_LINK_BUTTON[1]<br /><br />\r\n\r\nLink Text der zu sehen ist:<br /> \r\n<input type=text size=80 name=VALUE[1] value=\"REX_VALUE[1]\"><br /><br />\r\n','','','0','0','');
INSERT INTO `rex_modultyp` VALUES ('6','04 - Download','0','<?php\r\n\r\n$jahr_von = \'REX_VALUE[5]\';\r\n$monat_von = \'REX_VALUE[6]\';\r\n$tag_von = \'REX_VALUE[7]\';\r\n\r\n$jahr_bis = \'REX_VALUE[8]\';\r\n$monat_bis = \'REX_VALUE[9]\';\r\n$tag_bis = \'REX_VALUE[10]\';\r\n\r\nif ( $jahr_von != \'\'\r\n	AND $monat_von != \'\'\r\n	AND $tag_von != \'\'\r\n	AND $jahr_bis != \'\'\r\n	AND $monat_bis != \'\'\r\n	AND $tag_bis != \'\') {\r\n	\r\n	$von = mktime( 0, 0, 0, $monat_von, $tag_von, $jahr_von);\r\n	$bis = mktime( 23, 59, 0, $monat_bis, $tag_bis, $jahr_bis);\r\n	$jetzt = time();\r\n	\r\n	if( $jetzt >= $von && $jetzt <= $bis) {\r\n		if ($REX[\'REDAXO\'] != true) {\r\n	\r\n			if (!function_exists(\'Dateigroesse\')) {\r\n				function Dateigroesse($URL) {\r\n					$Groesse = filesize($URL);\r\n					if($Groesse<1000) {\r\n						return number_format($Groesse, 0, \",\", \".\").\" Bytes\";\r\n					}\r\n					elseif($Groesse<1000000) {\r\n						return number_format($Groesse/1024, 0, \",\", \".\").\" kB\";\r\n					}\r\n					else {\r\n						return number_format($Groesse/1048576, 0, \",\", \".\").\" MB\";\r\n					}\r\n				}\r\n			}\r\n	\r\n			if (!function_exists(\'parse_icon\')) {\r\n				function parse_icon($ext) {\r\n					switch (strtolower($ext)) {\r\n	\r\n					case \'doc\': return \'icon_doc.gif\';\r\n					case \'rtf\': return \'icon_doc.gif\';\r\n					case \'txt\': return \'icon_txt.gif\';\r\n					case \'xls\': return \'icon_xls.gif\';\r\n					case \'csv\': return \'icon_xls.gif\';\r\n					case \'ppt\': return \'icon_ppt.gif\';\r\n					case \'html\': return \'icon_html.gif\';\r\n					case \'htm\': return \'icon_html.gif\';\r\n					case \'php\': return \'icon_script.gif\';\r\n					case \'php3\': return \'icon_script.gif\';\r\n					case \'cgi\': return \'icon_script.gif\';\r\n					case \'pdf\': return \'icon_pdf.gif\';\r\n					case \'rar\': return \'icon_rar.gif\';\r\n					case \'zip\': return \'icon_zip.gif\';\r\n					case \'gz\': return \'icon_gz.gif\';\r\n					case \'jpg\': return \'icon_jpg.gif\';\r\n					case \'gif\': return \'icon_gif.gif\';\r\n					case \'png\': return \'icon_png.gif\';\r\n					case \'bmp\': return \'icon_image.gif\';\r\n					case \'tif\': return \'icon_image.gif\';\r\n					case \'exe\': return \'icon_binary.gif\';\r\n					case \'bin\': return \'icon_binary.gif\';\r\n					case \'avi\': return \'icon_mov.gif\';\r\n					case \'mpg\': return \'icon_mov.gif\';\r\n					case \'moc\': return \'icon_mov.gif\';\r\n					case \'asf\': return \'icon_mov.gif\';\r\n					case \'mp3\': return \'icon_sound.gif\';\r\n					case \'wav\': return \'icon_sound.gif\';\r\n					case \'org\': return \'icon_sound.gif\';\r\n				\r\n					default:\r\n					return \'icon_def.gif\';\r\n					}\r\n				}\r\n			}\r\n			$ext = substr(strrchr(\'REX_FILE[1]\', \'.\'), 1);\r\n			$parsed_icon = $REX[\'HTDOCS_PATH\'].\'/files/\'.parse_icon($ext);\r\n			$ooPic = OOMedia::getMediaByName (\'REX_FILE[1]\');\r\n			$fileDescription = $ooPic->getDescription();\r\n	\r\n			print \'\r\n				<div class=\"download-hdl\"><p>REX_VALUE[1]</p></div>\r\n				<div class=\"download-file\"><p><a href=\"\'.$REX[\'HTDOCS_PATH\'].\'files/REX_FILE[1]\" target=\"_blank\"><img src=\"\'.$parsed_icon.\'\" alt=\"Download\" /></a> REX_FILE[1] - \'.Dateigroesse($REX[\'HTDOCS_PATH\'].\"files/REX_FILE[1]\").\'</p></div>\';\r\n	\r\n			if ($fileDescription != \"\") { \r\n				print \'<div class=\"download-desc\"><p>\'.$fileDescription.\'</p></div>\';\r\n			}\r\n			else {\r\n				print \'<div class=\"download-without-desc\"> </div>\';\r\n			}\r\n		}\r\n		else {\r\n			print \"REX_VALUE[1]REX_VALUE[2]\";\r\n		}\r\n	} /* ende von if( $jetzt >= $von && $jetzt <= $bis)*/\r\n} \r\n?>','Bezeichnung des Download: \r\n<br><input type=text size=40 name=VALUE[1] value=\"REX_VALUE[1]\"><br /><br />\r\nREX_MEDIA_BUTTON[1]<br>\r\n<strong>Hinweis: Die Beschreibung des Downloads erfolgt im Medienpool!</strong><br />\r\n<br />\r\n<?php\r\n$jahr = date(\'Y\');\r\n$monat = date(\'m\');\r\n$tag = date(\'d\');\r\n\r\n$jahr_von_val = \'REX_VALUE[5]\';\r\n$monat_von_val = \'REX_VALUE[6]\';\r\n$tag_von_val = \'REX_VALUE[7]\';\r\n\r\n$jahr_bis_val = \'REX_VALUE[8]\';\r\n$monat_bis_val = \'REX_VALUE[9]\';\r\n$tag_bis_val = \'REX_VALUE[10]\';\r\n\r\nif ( empty($jahr_von_val)) $jahr_von_val = $jahr;\r\nif ( empty($monat_von_val)) $monat_von_val = $monat;\r\nif ( empty($tag_von_val)) $tag_von_val = $tag;\r\n\r\nif ( empty($jahr_bis_val)) $jahr_bis_val = $jahr +9;\r\nif ( empty($monat_bis_val)) $monat_bis_val = $monat;\r\nif ( empty($tag_bis_val)) $tag_bis_val = $tag;\r\n\r\n?>\r\nOnline von:<select name=\"VALUE[7]\" >\r\n<?php\r\nforeach (range(1,31) as $tag_von) {\r\n   echo \'<option value=\"\'.$tag_von.\'\" \';\r\n   \r\n   if ( $tag_von_val==$tag_von ) {\r\n      echo \'selected=\"selected\" \';\r\n   }\r\n   echo \'>\'.$tag_von.\'</option>\';\r\n}\r\n?>\r\n</select><select name=\"VALUE[6]\" >\r\n<?php\r\nforeach (range(1,12) as $monat_von) {\r\n   echo \'<option value=\"\'.$monat_von.\'\" \';\r\n   \r\n   if ( $monat_von_val==$monat_von ) {\r\n      echo \'selected=\"selected\" \';\r\n   }\r\n   echo \'>\'.$monat_von.\'</option>\';\r\n}\r\n?>\r\n\r\n</select>\r\n<select name=\"VALUE[5]\" >\r\n<?php\r\nforeach (range( $jahr,$jahr+9) as $jahr_von) {\r\n   echo \'<option value=\"\'.$jahr_von.\'\" \';\r\n   \r\n   if ( $jahr_von_val ==$jahr_von ) {\r\n      echo \'selected=\"selected\" \';\r\n   }\r\n   echo \'>\'.$jahr_von.\'</option>\';\r\n}\r\n?>\r\n\r\n</select><br /><br />\r\n\r\nOnline bis:<select name=\"VALUE[10]\" >\r\n<?php\r\nforeach (range(1,31) as $tag_bis) {\r\n   echo \'<option value=\"\'.$tag_bis.\'\" \';\r\n   \r\n   if ( $tag_bis_val==$tag_bis ) {\r\n      echo \'selected=\"selected\" \';\r\n   }\r\n   echo \'>\'.$tag_bis.\'</option>\';\r\n}\r\n?>\r\n</select><select name=\"VALUE[9]\" >\r\n<?php\r\nforeach (range(1,12) as $monat_bis) {\r\n   echo \'<option value=\"\'.$monat_bis.\'\" \';\r\n   \r\n   if ( $monat_bis_val==$monat_bis ) {\r\n      echo \'selected=\"selected\" \';\r\n   }\r\n   echo \'>\'.$monat_bis.\'</option>\';\r\n}\r\n?>\r\n\r\n</select>\r\n<select name=\"VALUE[8]\" >\r\n<?php\r\nforeach (range($jahr,$jahr+9) as $jahr_bis) {\r\n   echo \'<option value=\"\'.$jahr_bis.\'\" \';\r\n   \r\n   if ( $jahr_bis_val==$jahr_bis ) {\r\n      echo \'selected=\"selected\" \';\r\n   }\r\n   echo \'>\'.$jahr_bis.\'</option>\';\r\n}\r\n?>\r\n\r\n</select><br/><br/>\r\n\r\n<?php\r\n\r\nif ( $jahr_von_val != \'\'\r\n     && $monat_von_val != \'\'\r\n     && $tag_von_val != \'\'\r\n     && $jahr_bis_val != \'\'\r\n     && $monat_bis_val != \'\'\r\n     && $tag_bis_val != \'\') {\r\n  $von = mktime( 0, 0, 0, $monat_von_val, $tag_von_val, $jahr_von_val);\r\n  $bis  = mktime( 23, 59, 0, $monat_bis_val, $tag_bis_val, $jahr_bis_val);\r\n  $jetzt = time();\r\n\r\n  if( $jetzt >= $von && $jetzt <= $bis) {\r\n    $state = \'<span style=\"color: #00aa00;\">online</span>\';\r\n  } else {\r\n    $state = \'<span style=\"color: #aa0000;\">offline</span>\';\r\n  }\r\n\r\n  echo \'Download aufgrund der Datumseinstellungen \'.$state;\r\n} \r\n?>','','','0','0','');
INSERT INTO `rex_modultyp` VALUES ('7','05 - Teaser','0','<?php\r\n\r\n$itemsPerSide = \"REX_VALUE[1]\";\r\n$wordsPerArticle = \"REX_VALUE[2]\";\r\n\r\n// Nur im Frontend\r\n//if (!$REX[\'REDAXO\']):\r\n\r\n// Sortierfunktion\r\nif (!function_exists(sortArticlesByUpdateDate)) {\r\n	function sortArticlesByUpdateDate( $artA, $artB) {\r\n		$createA = $artA->_updatedate;\r\n		$createB = $artB->_updatedate;\r\n\r\n		if ( $createA == $createB) {\r\n			return 0;\r\n		}\r\n\r\n		return $createA > $createB ? -1 : 1;\r\n	}\r\n}\r\n\r\n$start = !empty($_GET[\'start\']) ? (int) $_GET[\'start\'] : 0; // Startitem\r\n$offset = $itemsPerSide; // 10 Items pro Seite\r\n$cats = array( REX_CATEGORY_ID); // alle Kategorien die Du \"indizieren\" willst\r\n\r\n$articles = array();\r\n\r\nforeach ( $cats as $catId) {\r\n  $cat = OOCategory::getCategoryById( $catId);\r\n \r\n  // CatIds die nicht funktionieren aussortieren\r\n  if( $cat === null) {\r\n     continue;\r\n  }\r\n\r\n  $cat_articles = $cat->getArticles( true);\r\n\r\n  foreach ( $cat_articles as $ooarticle) {\r\n     // keine Startartikel anzeigen\r\n    if ( $ooarticle->isStartPage()) {\r\n       continue;\r\n     }\r\n\r\n     // Damit keine Endlosschleife passiert, den Artikel der die Pagination setzt überspringen\r\n     if ( $ooarticle->getId() == REX_ARTICLE_ID) {\r\n        continue;\r\n     }\r\n     $articles[] = $ooarticle;\r\n  }\r\n}\r\n\r\n$articles_length = count( $articles);\r\n\r\n// Sortieren nach Erstellungsdatum\r\nif ( $articles_length > 0) {\r\n\r\n   usort( $articles, \"sortArticlesByUpdateDate\");\r\n}\r\n\r\n$article_list = array_slice( $articles, $start, $offset);\r\n\r\nforeach ( $article_list as $ooarticle) {\r\n	\r\n	if ($ooarticle->isOnline(true)) {\r\n		$articleId = $ooarticle->getId();\r\n\r\n		$article = new rex_article();\r\n		$article->setArticleId( $articleId);\r\n		$articleContent = $article->getArticle();\r\n	\r\n		// Leerzeichen entfernen am Anfang und Ende des Strings\r\n		$articleContent = trim($articleContent);\r\n		\r\n		// Leerzeichen vor </p> einfuegen.\r\n		// Sonst wird das letzte Wort eines Absatzes und das erste Wort \r\n		// des nachfolgenden Absatzes als ein Wort erkannt\r\n		$articleContent = str_replace(\"</p>\", \" </p>\", $articleContent);\r\n		\r\n		// Leerzeichen vor <br /> einfuegen.\r\n		// Sonst wird das letzte Wort einer Zeile und das erste Wort \r\n		// des nachfolgenden Zeile als ein Wort erkannt\r\n		$articleContent = str_replace(\"<br />\", \" <br />\", $articleContent);\r\n		\r\n		$output = \"\";\r\n		$words = explode(\" \",$articleContent);\r\n		$wordsCount = count($words);\r\n	\r\n		if ($wordsCount < $wordsPerArticle) $wEnd = $wordsCount;\r\n		else  $wEnd = $wordsPerArticle;\r\n				\r\n		for ($w=0;$w<$wEnd;$w++) {\r\n			$output .= $words[$w].\" \";\r\n		}\r\n		\r\n		// Leerzeichen entfernen am Anfang und Ende des Strings	\r\n		$output = trim($output);\r\n	\r\n		$isCloseParagraph = substr($output,-4);\r\n		$link = \'<a href=\"\'.rex_getUrl($articleId).\'\">...mehr</a>\';\r\n		$newString = $link.\'</p>\';\r\n	\r\n	\r\n		if ($isCloseParagraph == \'</p>\') {\r\n			$output = substr_replace($output,$newString,-4);\r\n		}\r\n		else {\r\n			$output .= $newString;\r\n		}\r\n	\r\n		print \'<div class=\"teaser\">\'.$output.\'</div>\';\r\n	}\r\n}\r\n\r\n$prevStart = $start - $offset;\r\nif ( $prevStart < 0)\r\n{\r\n   $prevStart = \'\';\r\n}\r\n\r\n$nextStart = $start + $offset;\r\nif ( $nextStart >= $articles_length)\r\n{\r\n   $nextStart = \'\';\r\n}\r\n\r\n$link_format = \'<a href=\"index.php?article_id=REX_ARTICLE_ID&start=%s\">%s</a>\';\r\n\r\n// Vorherige Seite Link\r\nif ( $prevStart !== \'\') {\r\n  $backLink = sprintf( $link_format, $prevStart, \'<< vorherige Seite\');\r\n}\r\n\r\n// Nächste Seite\r\nif ( $nextStart !== \'\') {\r\n  $nextLink = sprintf( $link_format, $nextStart, \'n&auml;chste Seite >>\');\r\n}\r\n\r\nif ($backLink != \"\" AND $nextLink != \"\") {\r\n	print \'<div class=\"teaserNav\"><p>\'.$backLink.\'&nbsp;|&nbsp;\'.$nextLink.\'</p></div>\';\r\n}\r\nelseif ($nextLink != \"\") {\r\n	print \'<div class=\"teaserNav\"><p>\'.$nextLink.\'</p></div>\';\r\n}\r\nelseif ($backLink != \"\") {\r\n	print \'<div class=\"teaserNav\"><p>\'.$backLink.\'</p></div>\';\r\n}\r\n\r\n//endif;\r\n\r\n?>\r\n','<strong>Anzahl der Artikel pro Seite</strong><br />\r\n<input name=\"VALUE[1]\" value=\"REX_VALUE[1]\" class=\"inp100\" /><br />\r\n<br />\r\n<strong>Anzahl der Wörter pro Artikel</strong><br />\r\n<input name=\"VALUE[2]\" value=\"REX_VALUE[2]\" class=\"inp100\" /><br />','','','0','0','');
INSERT INTO `rex_modultyp` VALUES ('9','05 - Articlelist','0','<?\r\n\r\n$cat = OOCategory::getCategoryById($this->getValue(\"category_id\"));\r\n$article = $cat->getArticles();\r\n\r\n\r\nif (is_array($article)) {\r\n	foreach ($article as $var) {\r\n\r\n	$articleId		= $var->getId();\r\n	$articleName		= $var->getName();\r\n	$articleDescription	= $var->getDescription();\r\n	\r\n		if ($var->getValue(_startpage) != 1) {\r\n			echo \'<div class=\"article-list\"><h2>\'.$articleName.\'</h2><p>\'.$articleDescription.\'</p><p><a href=\"\'.rex_getUrl($articleId).\'\">mehr >></a></p></div>\';\r\n		}\r\n	}\r\n}\r\n?>','','','','0','0','');
INSERT INTO `rex_modultyp` VALUES ('10','05 - Categorylist','0','<?\r\n$cat = OOCategory::getCategoryById($this->getValue(category_id));\r\n$cats = $cat->getChildren();\r\n\r\nprint \'<ul>\';\r\nif (is_array($cats)) {\r\n	foreach ($cats as $cat) {\r\n\r\n	$catId			= $cat->getId();\r\n	$catName		= $cat->getName();\r\n	\r\n	print \'<li><a href=\"\'.rex_getUrl($catId).\'\">\'.$catName.\'</a></li>\';\r\n	}\r\n}\r\nprint \'</ul>\';\r\n?>','','','','0','0','');
INSERT INTO `rex_modultyp` VALUES ('11','07 - sitemap','0','<?php\r\n\r\necho \'<ul class=\"sitemap1st\">\';\r\nforeach (OOCategory::getRootCategories() as $lev1):\r\n	$catId = $lev1->getId();\r\n\r\n	\r\n	if($lev1->isOnline()):\r\n	\r\n		$lev1Size = sizeof($lev1->getChildren());\r\n		\r\n		print \'<li><a href=\"\'.$lev1->getUrl().\'\">\'.$lev1->getName().\'</a>\';\r\n\r\n		$lev1Size = sizeof($lev1->getChildren());\r\n		if($lev1Size != \"0\"):\r\n			echo \'<ul class=\"sitemap2nd\">\';\r\n			foreach ($lev1->getChildren() as $lev2):\r\n\r\n				if ($lev2->isOnline()):\r\n					print \'<li><a href=\"\'.$lev2->getUrl().\'\">\'.$lev2->getName().\'</a></li>\';\r\n				endif;\r\n			endforeach;\r\n			echo \'</ul>\';\r\n		endif;\r\n		\r\n\r\n		echo \'</li>\';		\r\n	endif;\r\nendforeach;\r\necho \'</ul>\';\r\n\r\n?>','','','admin','0','1181206023','');
INSERT INTO `rex_template` VALUES ('1','','default','<?php\r\n\r\n// Notices ausschalten\r\n// error_reporting(E_ALL ^ E_NOTICE); \r\n\r\n//	Setting\r\n////////////////////////////////////////////////////////////////////////////////\r\n\r\n// Navigation Startseite im vertikalen Block\r\nif ($REX[\'START_ARTICLE_ID\'] == $this->getValue(\"article_id\")) {\r\n	$navTemplateId = \"3\";\r\n} \r\n// Navigation Inhaltsseiten horizontal / vertikale	\r\nelse {\r\n	$navTemplateId = \"2\";\r\n}\r\n\r\n//	Code\r\n////////////////////////////////////////////////////////////////////////////////\r\n\r\n$article = OOArticle::getArticleById($REX[\'START_ARTICLE_ID\'], $REX[\'CUR_CLANG\']);\r\n$articleK = $article->getValue(\"_keywords\");\r\n$articleD = $article->getDescription();\r\n\r\n$navTemplate = new rex_template($navTemplateId);	\r\ninclude $navTemplate->getFile();\r\n	\r\nif($this->getValue(\"description\") != \"\") {\r\n	$meta_beschreibung = htmlspecialchars($this->getValue(\"description\"));\r\n} else {\r\n	$meta_beschreibung = htmlspecialchars($articleD);\r\n}\r\n	\r\nif($this->getValue(\"keywords\")!= \"\") {\r\n	$meta_suchbegriffe = htmlspecialchars($this->getValue(\"keywords\"));\r\n} else {\r\n	$meta_suchbegriffe = htmlspecialchars($articleK);\r\n} \r\n\r\n?><!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Strict//EN\"\r\n	\"http://www.w3.org/TR/xhtml1/DTD/xhtml1-strict.dtd\">\r\n\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\" xml:lang=\"de\" lang=\"de\">\r\n<head>\r\n	<meta http-equiv=\"Content-Type\" content=\"text/html; charset=iso-8859-1\"/>\r\n\r\n	<title><?php print $REX[\'SERVERNAME\'].\' | \'.$this->getValue(\"name\"); ?></title>\r\n	<meta name=\"keywords\" content=\"<?php print $meta_suchbegriffe; ?>\" />\r\n	<meta name=\"description\" content=\"<?php print $meta_beschreibung; ?>\" />\r\n\r\n	<link rel=\"stylesheet\" type=\"text/css\" href=\"<?php echo $REX[\'HTDOCS_PATH\'] ?>files/main.css\" media=\"screen\" />\r\n	<link rel=\"stylesheet\" type=\"text/css\" href=\"<?php echo $REX[\'HTDOCS_PATH\'] ?>files/navigation.css\" media=\"screen\" />\r\n	<link rel=\"stylesheet\" type=\"text/css\" href=\"<?php echo $REX[\'HTDOCS_PATH\'] ?>files/content.css\" media=\"screen\" />\r\n	\r\n<?php \r\n\r\n// Definierte Unterschiede Startseite - Inhaltsseiten\r\n\r\nif ($REX[\'START_ARTICLE_ID\'] == $this->getValue(\"article_id\")) {\r\n	print \'<link rel=\"stylesheet\" type=\"text/css\" href=\"\'.$REX[\'HTDOCS_PATH\'].\'files/start.css\" media=\"screen\" />\';\r\n} else {\r\n	print \'<link rel=\"stylesheet\" type=\"text/css\" href=\"\'.$REX[\'HTDOCS_PATH\'].\'files/default.css\" media=\"screen\" />\';\r\n}\r\n\r\n?>\r\n</head>\r\n\r\n<body class=\"mainPage\">\r\n	<div>\r\n		<a name=\"top\" id=\"top\"></a>\r\n	</div>\r\n\r\n	<div id=\"site-content\">\r\n		<div id=\"column\">\r\n			\r\n			<!-- start header -->\r\n			<div id=\"header\">\r\n				<div id=\"logo\">\r\n					<a href=\"<?php echo $REX[\'HTDOCS_PATH\'] ?>index.php\" title=\"Zur&uuml;ck zur Startseite\">REDAXO Demo</a>\r\n				</div>\r\n				<?php\r\n					if ($navHeader != \"\") {\r\n						print \'<div id=\"nav-header\">\'.$navHeader.\'</div>\';\r\n					}\r\n				?>\r\n			</div><!-- end header -->\r\n			\r\n			<!-- start content -->\r\n			<div id=\"content\">\r\n				<div id=\"main-content\">\r\n					<div id=\"nav\">\r\n					<?php print $navLeftCol; ?>\r\n						<p class=\"copy\">&copy; by <a href=\"http://www.redaxo.de\">REDAXO</a></p>\r\n\r\n						<div id=\"donate\">\r\n							<form action=\"https://www.paypal.com/cgi-bin/webscr\" method=\"post\">\r\n							<fieldset>\r\n							<input type=\"hidden\" name=\"cmd\" value=\"_s-xclick\" />\r\n							<input type=\"image\" src=\"<?php echo $REX[\'HTDOCS_PATH\'] ?>files/donate_redaxo.gif\" name=\"submit\" alt=\"Zahlen Sie mit PayPal - schnell, kostenlos und sicher!\" />\r\n							<input type=\"hidden\" name=\"encrypted\" value=\"-----BEGIN PKCS7----------END PKCS7-----\r\n							\" />\r\n							</fieldset>\r\n							</form>\r\n						</div>\r\n					</div>\r\n\r\n					<div id=\"main\">\r\n						<div id=\"main-block\">\r\n							<div id=\"main-teaser\">\r\n								Slogan: Einfach, flexibel, sinnvoll\r\n							</div>\r\n\r\n							<div id=\"main-content-block\">\r\n								<?php\r\n									print \'<p id=\"breadcrumb\">\'.$breadcrumb.\'</p>\';\r\n\r\n									print $this->getArticle();\r\n								?>\r\n							</div>\r\n						</div>\r\n					</div>\r\n					<br class=\"clear\" />\r\n				</div>\r\n			</div><!-- end content -->\r\n\r\n			<div id=\"footer\">\r\n				<p class=\"floatRight\"><a href=\"http://www.redaxo.de\">REDAXO CMS</a> - SIMPLE DEMO | XHTML 1.0 Strict | pictures by <a href=\"http://www.photocase.com\">photocase.com</a></p>\r\n				\r\n				<br class=\"clear\" />\r\n			</div>\r\n		</div>\r\n	</div>\r\n<div style=\"display:none;\">Eigene Templates sind besser - REDAXO</div>\r\n</body>\r\n</html>\r\n','1','admin07','admin','1174332595','1192235452','a:1:{s:5:\"ctype\";a:0:{}}');
INSERT INTO `rex_template` VALUES ('2','','navigation - horizontal - vertical','<?php\r\n// EXPLODE PATH\r\n$PATH = explode(\"|\",$this->getValue(\"path\").$this->getValue(\"article_id\").\"|\");\r\n\r\n// GET CURRENTS\r\n$path1 = $PATH[1];\r\n$path2 = $PATH[2];\r\n$path3 = $PATH[3];\r\n\r\n\r\n/* START 1st level categories */\r\n$navHeader .= \'<ul>\';\r\n$navLeftCol .= \'<div id=\"navheight_horiz_vert\">\';\r\nforeach (OOCategory::getRootCategories() as $lev1) {\r\n		\r\n	if ($lev1->isOnline()):\r\n		if ($lev1->getId() == $path1) {\r\n			$navHeader .= \'<li><a class=\"current\" href=\"\'.$lev1->getUrl().\'\">\'.$lev1->getName().\'</a>\';\r\n			if ($lev1->getId() != \"1\") {\r\n				$sitePath .= \' &#8250;&#8250; <a href=\"\'.$lev1->getUrl().\'\">\'.$lev1->getName().\'</a>\';\r\n			}\r\n			$titlePath .= \' >> \'.$lev1->getName();\r\n		}\r\n		// 2nd level - no active link\r\n		else {\r\n			$navHeader .= \'<li><a href=\"\'.$lev1->getUrl().\'\">\'.$lev1->getName().\'</a>\';\r\n		}\r\n		\r\n			\r\n		// 1st level had categories? -> go on \r\n		$lev1Size = sizeof($lev1->getChildren());\r\n	\r\n		if ($lev1->getId() == $path1) {\r\n		if ($lev1Size != \"0\"):\r\n			$navLeftCol .= \'<ul class=\"nav1st\">\';\r\n			$navLeftCol .= \'<li>\'.$lev1->getName();\r\n\r\n			$navLeftCol .= \'<ul class=\"nav2nd\">\';\r\n\r\n			// START 2nd level categories \r\n			foreach ($lev1->getChildren() as $lev2):\r\n						\r\n				if ($lev2->isOnline()):\r\n	\r\n				// 2nd level - active link\r\n					if ($lev2->getId() == $path2) {\r\n						$navLeftCol .= \'<li class=\"active\"><a href=\"\'.$lev2->getUrl().\'\">\'.$lev2->getName().\'</a></li>\';\r\n						$sitePath .= \' &#8250;&#8250; <a href=\"\'.$lev2->getUrl().\'\">\'.$lev2->getName().\'</a>\';\r\n						$titlePath .= \' >> \'.$lev2->getName();\r\n					}\r\n					// 2nd level - no active link\r\n					else {\r\n						$navLeftCol .= \'<li><a href=\"\'.$lev2->getUrl().\'\">\'.$lev2->getName().\'</a></li>\';\r\n					}\r\n			\r\n				endif;			\r\n				\r\n			endforeach;\r\n			// END 2nd level categories \r\n			$navLeftCol .= \'</ul>\';\r\n			$navLeftCol .= \'</li></ul>\';\r\n		endif; // END by if ($lev1Size != \"0\"):\r\n		}\r\n		$navHeader .= \'</li>\';\r\n\r\n	endif; // END by if ($lev1->isOnline()) \r\n}\r\n$navHeader .= \'</ul>\';\r\n$navLeftCol .= \'</div>\';\r\n// END 1st level categories \r\n\r\nif ($REX[\'CUR_CLANG\'] == 1) $breadcrumb = \'You are here: <a href=\"/index.php?clang=1\">Startpage</a> \'.$sitePath;\r\nelse $breadcrumb = \'Sie befinden sich hier: <a href=\"/index.php\">Startseite</a> \'.$sitePath;\r\n\r\n\r\n\r\n?>','0','admin07','admin','1174333539','1192276283','a:1:{s:5:\"ctype\";a:0:{}}');
INSERT INTO `rex_template` VALUES ('3','','navigation - vertical','<?php\r\n// EXPLODE PATH\r\n$PATH = explode(\"|\",$this->getValue(\"path\").$this->getValue(\"article_id\").\"|\");\r\n\r\n// GET CURRENTS\r\n$path1 = $PATH[1];\r\n$path2 = $PATH[2];\r\n$path3 = $PATH[3];\r\n\r\n\r\n/* START 1st level categories */\r\n$navLeftCol .= \'<div id=\"navheight_vert\">\';\r\n$navLeftCol .= \'<ul class=\"nav1st\">\';\r\nforeach (OOCategory::getRootCategories() as $lev1) {\r\n		\r\n	if ($lev1->isOnline()):\r\n		if ($lev1->getId() == $path1) {\r\n			$navLeftCol .= \'<li><a class=\"current\" href=\"\'.$lev1->getUrl().\'\">\'.$lev1->getName().\'</a></li>\';\r\n			if ($lev1->getId() != \"1\") {\r\n				$sitePath .= \' &#8250;&#8250; <a href=\"\'.$lev1->getUrl().\'\">\'.$lev1->getName().\'</a></li>\';\r\n			}\r\n			$titlePath .= \' >> \'.$lev1->getName();\r\n		}\r\n		// 1st level - no active link\r\n		else {\r\n			$navLeftCol .= \'<li><a href=\"\'.$lev1->getUrl().\'\">\'.$lev1->getName().\'</a></li>\';\r\n		}\r\n			\r\n		// 1st level had categories? -> go on \r\n		$lev1Size = sizeof($lev1->getChildren());\r\n		\r\n	if ($lev1->getId() == $path1) {\r\n		if ($lev1Size != \"0\"):\r\n			$navLeftCol .= \'<ul class=\"nav2nd\">\';\r\n\r\n\r\n			// START 2nd level categories \r\n			foreach ($lev1->getChildren() as $lev2):\r\n						\r\n				if ($lev2->isOnline()):\r\n	\r\n				// 2nd level - active link\r\n					if ($lev2->getId() == $path2) {\r\n						$navLeftCol .= \'<li class=\"active\"><a href=\"\'.$lev2->getUrl().\'\">\'.$lev2->getName().\'</a></li>\';\r\n						$sitePath .= \' &#8250;&#8250; <a href=\"\'.$lev2->getUrl().\'\">\'.$lev2->getName().\'</a>\';\r\n						$titlePath .= \' >> \'.$lev2->getName();\r\n					}\r\n					// 2nd level - no active link\r\n					else {\r\n						$navLeftCol .= \'<li><a href=\"\'.$lev2->getUrl().\'\">\'.$lev2->getName().\'</a></li>\';\r\n					}\r\n			\r\n				endif;			\r\n				\r\n			endforeach;\r\n			// END 2nd level categories \r\n		\r\n			$navLeftCol .= \'</ul>\';\r\n		endif; // END by if ($lev1Size != \"0\"):\r\n	\r\n	}\r\n	endif; // END by if ($lev1->isOnline()) \r\n}\r\n$navLeftCol .= \'</ul>\';\r\n$navLeftCol .= \'</div>\';\r\n// END 1st level categories \r\n\r\nif ($REX[\'CUR_CLANG\'] == 1) $breadcrumb = \'You are here: <a href=\"/index.php?clang=1\">Startpage</a> \'.$sitePath;\r\nelse $breadcrumb = \'Sie befinden sich hier: <a href=\"/index.php\">Startseite</a> \'.$sitePath;\r\n\r\n\r\n?>','0','admin07','admin07','1174490885','1174507785','');
