INSERT INTO rex_modultyp VALUES ('4','','10.01 - Artikelkommentarbox','0','<br><table width=100% cellpadding=0 cellspacing=0 border=0>||||||+N+||||||<form action=index.php method=post>||||||+N+||||||<input type=hidden name=article_id value=REX_ARTICLE_ID>||||||+N+||||||<input type=hidden name=FORM[article_add_comment] value=1>||||||+N+||||||<tr>||||||+N+||||||||||||+N+||||||<td class=grey valign=top width=15><img src=/pics/white.gif width=5 height=5><br><img src=/pics/leer.gif width=15 height=100></td>||||||+N+||||||||||||+N+||||||<td valign=top class=grey><img src=/pics/leer.gif width=325 height=15><br><?||||||+N+||||||||||||+N+||||||$gc = new sql;||||||+N+||||||$gc->setQuery(\"select * from rex__article_comment ||||||+N+||||||left join rex__user on rex__user.id=rex__article_comment.user_id ||||||+N+||||||where ||||||+N+||||||rex__article_comment.article_id=\'REX_ARTICLE_ID\' and ||||||+N+||||||rex__article_comment.status=1 ||||||+N+||||||order by ||||||+N+||||||rex__article_comment.stamp LIMIT 8\");||||||+N+||||||||||||+N+||||||echo \"<b>Kommentarbox:</b> \";||||||+N+||||||||||||+N+||||||if ($gc->getRows()>0) echo \" Bisherige Kommentare<br>\";||||||+N+||||||else echo \" Zum Kommentieren bitte einloggen!<br>\";||||||+N+||||||||||||+N+||||||echo \"<hr noshade style=\'width:100%;color:#cccccc;height:1px;text-align:left;border:0px;\'>\";||||||+N+||||||||||||+N+||||||for ($i=0;$i<$gc->getRows();$i++)||||||+N+||||||{||||||+N+|||||| echo \"<b>\".date(\"d M Y\",$gc->getValue(\"rex__article_comment.stamp\")).\" | </b>\";||||||+N+|||||| echo \"<a href=index.php?article_id=16&FORM[user_id]=\".$gc->getValue(\"rex__user.id\").\" class=red>\".$gc->getValue(\"rex__user.login\").\"</a>\";||||||+N+|||||| echo \"<br>\".nl2br(htmlentities($gc->getValue(\"rex__article_comment.comment\"))).\"<br><hr noshade style=\'width:100%;color:#cccccc;height:1px;text-align:left;border:0px;\'>\";||||||+N+|||||| $gc->next();||||||+N+||||||}||||||+N+||||||||||||+N+||||||if ($FORM[USR])||||||+N+||||