INSERT INTO rex_modultyp VALUES ('9','','03.02 - Artikeliste als Kalender','0','<?||||||+N+||||||||||||+N+||||||$category_id = $this->getValue(\"category_id\");||||||+N+||||||$path  = $this->getValue(\"path\");||||||+N+||||||preg_match_all(\"/-([0-9]+)/i\",$path,$match);||||||+N+||||||$spath = $match[0][0];||||||+N+||||||||||||+N+||||||$GC = new sql;||||||+N+||||||// $GC->debugsql = 1;||||||+N+||||||$GC->setQuery(\"select * from rex_article ||||||+N+||||||where ||||||+N+||||||(rex_article.path like \'$spath-%\' or rex_article.path=\'$spath\') and ||||||+N+||||||rex_article.startpage=0 and rex_article.status = 1 ||||||+N+||||||order by rex_article.prior,rex_article.name\");||||||+N+||||||||||||+N+||||||for ($i=0;$i<$GC->getRows();$i++)||||||+N+||||||{||||||+N+|||||| $KAL[$GC->getValue(\"rex_article.online_von\")] = $GC->getValue(\"rex_article.id\");||||||+N+|||||| $KALNAME[$GC->getValue(\"rex_article.online_von\")] = $GC->getValue(\"rex_article.name\");||||||+N+|||||| $GC->next();||||||+N+||||||}||||||+N+||||||||||||+N+||||||||||||+N+||||||// ----- KALENDER||||||+N+||||||||||||+N+||||||if ($FORM[date]!=\"\") ||||||+N+||||||{ ||||||+N+|||||| $FORM[year] = substr($FORM[date],0,4); ||||||+N+|||||| $FORM[month] = substr($FORM[date],4,2); ||||||+N+|||||| $FORM[day] = substr($FORM[date],6,2); ||||||+N+||||||} ||||||+N+||||||||||||+N+||||||if (!checkdate($FORM[month],$FORM[day],$FORM[year])) ||||||+N+||||||||||||+N+||||||{ ||||||+N+|||||| $FORM[year] = date(\"Y\"); ||||||+N+|||||| $FORM[month] = date(\"m\"); ||||||+N+|||||| $FORM[day] = date(\"d\"); ||||||+N+||||||} ||||||+N+||||||||||||+N+||||||$FORM[date] = $FORM[year].$FORM[month].$FORM[day]; ||||||+N+||||||||||||+N+||||||$today = date(\"Ymd\");||||||+N+||||||$FORM[link] = \"index.php?article_id=REX_ARTICLE_ID\";||||||+N+||||||$link = $FORM[link];||||||+N+||||||$month_days = date(\"j\",mktime(0,0,0,$FORM[month]+1,1,$FORM[year])-1);||||||+N+||||||$month_before = $FORM[month]-1;||||||+N+||||||$month_later = $FORM[month]+1;