INSERT INTO rex_article_slice VALUES ('52','0','PRODUKT A','Mein Produktbeschreibungt.... REDAXO baut auf der populären Scriptsprache PHP und der Datenbank MySQL auf.||||||+N+||||||||||||+N+||||||Gegenwärtig wird REDAXO als professionelles Redaktionssystem von zahlreichen Unternehmen und Institutionen für unterschiedliche Informations Management Lösungen eingesetzt.||||||+N+||||||||||||+N+||||||Damit dies auch so bleibt, treibt Deine Kreativität und Dein Einsatz die Entwicklung von REDAXO voran. Wir freuen uns auf Dein Feedback.','i','meine produktbildbeschreibung','Zurück zur Produktübersicht','','','','1','','banner.jpg','','','','','','','','','','2','0','0','0','0','0','0','0','0','0','','','24','12');
