INSERT INTO rex_modultyp VALUES ('9','','03.02 - Artikeliste als Kalender','0','<?||||||+N+||||||||||||+N+||||||$category_id = $this->getValue(\"category_id\");||||||+N+||||||$path  = $this->getValue(\"path\");||||||+N+||||||preg_match_all(\"/-([0-9]+)/i\",$path,$match);||||||+N+||||||$spath = $match[0][0];||||||+N+||||||||||||+N+||||||$GC = new sql;||||||+N+||||||// $GC->debugsql = 1;||||||+N+||||||$GC->setQuery(\"select * from rex_article ||||||+N+||||||where ||||||+N+||||||(rex_article.path like \'$spath-%\' or rex_article.path=\'$spath\') and ||||||+N+||||||rex_article.startpage=0 and rex_article.status = 1 ||||||+N+||||||order by rex_article.prior,rex_article.name\");||||||+N+||||||||||||+N+||||||for ($i=0;$i<$GC->getRows();$i++)||||||+N+||||||{||||||+N+|||||| $KAL[$GC->getValue(\"rex_article.online_von\")] = $GC->getValue(\"rex_article.id\");||||||+N+|||||| $KALNAME[$GC->getValue(\"rex_article.online_von\")] = $GC->getValue(\"rex_article.name\");||||||+N+|||||| $GC->next();||||||+N+||||||}||||||+N+||||||||||||+N+||||||||||||+N+||||||// ----- KALENDER||||||+N+||||||||||||+N+||||||if ($FORM[date]!=\"\") ||||||+N+||||||{ ||||||+N+|||||| $FORM[year] = substr($FORM[date],0,4); ||||||+N+|||||| $FORM[month] = substr($FORM[date],4,2); ||||||+N+|||||| $FORM[day] = substr($FORM[date],6,2); ||||||+N+||||||} ||||||+N+||||||||||||+N+||||||if (!checkdate($FORM[month],$FORM[day],$FORM[year])) ||||||+N+||||||||||||+N+||||||{ ||||||+N+|||||| $FORM[year] = date(\"Y\"); ||||||+N+|||||| $FORM[month] = date(\"m\"); ||||||+N+|||||| $FORM[day] = date(\"d\"); ||||||+N+||||||} ||||||+N+||||||||||||+N+||||||$FORM[date] = $FORM[year].$FORM[month].$FORM[day]; ||||||+N+||||||||||||+N+||||||$today = date(\"Ymd\");||||||+N+||||||$FORM[link] = \"index.php?article_id=REX_ARTICLE_ID\";||||||+N+||||||$link = $FORM[link];||||||+N+||||||$month_days = date(\"j\",mktime(0,0,0,$FORM[month]+1,1,$FORM[year])-1);||||||+N+||||||$month_before = $FORM[month]-1;||||||+N+||||||$month_later = $FORM[month]+1;||||||+N+||||||$year_before = $FORM[year];||||||+N+||||||$year_later = $FORM[year];||||||+N+||||||if ($month_before<1){ $month_before = 12; $year_before= $year_before-1;}||||||+N+||||||if ($month_later>12){ $month_later = 1; $year_later= $year_later+1;}||||||+N+||||||if ($month_before<10){ $month_before = \"0$month_before\"; }||||||+N+||||||if ($month_later<10){ $month_later = \"0$month_later\"; }||||||+N+||||||$before = $year_before.$month_before.\"01\";||||||+N+||||||$later  = $year_later.$month_later.\"01\";||||||+N+||||||||||||+N+||||||$KALENDER .= \"<table width=150 cellpadding=2 cellspacing=1 border=0>\";||||||+N+||||||$KALENDER .= \"<tr><td colspan=3><a href=$link&FORM[date]=$before class=calhead><b>&laquo;</b></a>&nbsp;<a href=$link&FORM[date]=$later class=calhead><b>&raquo;</b></a></td><td colspan=4 align=right class=caldate>\".$FORM[month].\"/\".$FORM[year].\"</td></tr>\";||||||+N+||||||$KALENDER .= \"<tr><td class=calhead>Mo</td><td class=calhead>Tu</td><td class=calhead>We</td><td class=calhead>Th</td><td class=calhead>Fr</td><td class=calhead>Sa</td><td class=calhead>Su</td></tr>\";||||||+N+||||||	||||||+N+||||||$k = 1;||||||+N+||||||for ($i=1;$i<7;$i++)||||||+N+||||||{||||||+N+|||||| $KALENDER .= \"<tr>\";||||||+N+|||||| for($j=0;$j<7;$j++)||||||+N+|||||| {||||||+N+||||||  $k = $k + 0;||||||+N+||||||  if ($k<10) $k=\"0$k\";||||||+N+||||||  if ($FORM[month]<10) $comp_month=\"\".$FORM[month]; ||||||+N+||||||  else $comp_month=$FORM[month];||||||+N+||||||  $which_weekday = date(\"w\",mktime(0,0,0,$FORM[month],$k-1,$FORM[year]));||||||+N+||||||  if ($which_weekday == $j and $month_days>=$k)||||||+N+||||||  { 	||||||+N+||||||   $loop_day = $FORM[year].\"$comp_month$k\";||||||+N+||||||   $KALENDER .= \"<td align=center\";||||||+N+||||||   if ($FORM[date] == $loop_day) $KALENDER .= \" class=calbg1>\";||||||+N+||||||   elseif ($today == $loop_day) $KALENDER .= \" class=calbg2>\";||||||+N+||||||   elseif ($j == 5 or $j == 6) $KALENDER .= \" class=calbg3>\";||||||+N+||||||   else $KALENDER .= \" class=calbg4>\";||||||+N+||||||   if ($KAL[$loop_day] != \"\") $KALENDER .= \"<a href=index.php?article_id=\".$KAL[$loop_day].\"&FORM[date]=$loop_day class=cala>$k</a>\";||||||+N+||||||   else $KALENDER .= \"$k\";||||||+N+||||||   $KALENDER .= \"</td>\";||||||+N+||||||   $k++;||||||+N+||||||  }else||||||+N+||||||  {||||||+N+||||||   $KALENDER .= \"<td></td>\";||||||+N+||||||  }||||||+N+|||||| }||||||+N+|||||| $KALENDER .= \"</tr>\";||||||+N+|||||| if ($month_days<$k) break;||||||+N+||||||}||||||+N+||||||$KALENDER .= \"</table>\";||||||+N+||||||||||||+N+||||||// ----- / KALENDER||||||+N+||||||||||||+N+||||||echo \"<div id=alistekal style=\'width:180px;float:left;\'>$KALENDER</div>\";||||||+N+||||||||||||+N+||||||?>','','<br><br>','','0','0');
