
DROP TABLE IF EXISTS rex__article_comment;
DROP TABLE IF EXISTS rex__board;
DROP TABLE IF EXISTS rex__user;
DROP TABLE IF EXISTS rex__user_comment;
DROP TABLE IF EXISTS rex__user_mail;
DROP TABLE IF EXISTS rex_article;
DROP TABLE IF EXISTS rex_article_slice;
DROP TABLE IF EXISTS rex_article_type;
DROP TABLE IF EXISTS rex_category;
DROP TABLE IF EXISTS rex_email;
DROP TABLE IF EXISTS rex_file;
DROP TABLE IF EXISTS rex_help;
DROP TABLE IF EXISTS rex_modultyp;
DROP TABLE IF EXISTS rex_template;
DROP TABLE IF EXISTS rex_user;
CREATE TABLE rex_user ( user_id int(11) NOT NULL auto_increment, name varchar(255) NOT NULL default '', description text NOT NULL, login varchar(50) NOT NULL default '', psw varchar(50) NOT NULL default '', status varchar(5) NOT NULL default '', rights text NOT NULL, login_tries tinyint(4) NOT NULL default '0', createdate int(11) NOT NULL default '0', updatedate int(11) NOT NULL default '0', lasttrydate int(11) NOT NULL default '0', session_id varchar(255) NOT NULL default '', PRIMARY KEY  (user_id)) TYPE=MyISAM;
DROP TABLE IF EXISTS rex_action;
DROP TABLE IF EXISTS rex_file_category;
DROP TABLE IF EXISTS rex__session;

CREATE TABLE rex__article_comment ( id int(11) NOT NULL  auto_increment, user_id int(11) NULL DEFAULT 0  , article_id int(11) NULL DEFAULT 0  , comment text NULL  , stamp int(11) NULL DEFAULT 0  , status tinyint(4) NULL DEFAULT 0  , PRIMARY KEY(id))TYPE=MyISAM;
CREATE TABLE rex__board ( message_id int(11) NOT NULL  auto_increment, re_message_id int(11) NULL DEFAULT 0  , board_id varchar(255) NULL  , user_id int(11) NULL DEFAULT 0  , replies int(11) NULL DEFAULT 0  , last_entry varchar(14) NULL  , subject varchar(255) NULL  , message text NULL  , stamp varchar(14) NULL  , status int(11) NULL DEFAULT 1  , anonymous_user varchar(50) NULL  , PRIMARY KEY(message_id))TYPE=MyISAM;
CREATE TABLE rex__session ( session varchar(50) NOT NULL  , user_id int(11) NULL DEFAULT 0  , name varchar(50) NULL  , stamp int(11) NULL DEFAULT 0  , PRIMARY KEY(session))TYPE=MyISAM;
CREATE TABLE rex__user ( id int(11) NOT NULL  auto_increment, login varchar(255) NULL  , psw varchar(255) NULL  , email varchar(255) NULL  , name varchar(255) NULL  , firstname varchar(255) NULL  , sex char(1) NULL  , singlestatus varchar(25) NULL DEFAULT 0  , street varchar(255) NULL  , zip varchar(5) NULL  , city varchar(255) NULL  , phone varchar(255) NULL  , fax varchar(255) NULL  , mobil varchar(255) NULL  , posx int(11) NULL DEFAULT 0  , posy int(11) NULL DEFAULT 0  , file varchar(255) NULL  , birthday varchar(14) NULL DEFAULT 0  , interests text NULL  , motto text NULL  , ilike text NULL  , aboutme text NULL  , size varchar(255) NULL  , wheight varchar(255) NULL  , color_eyes varchar(255) NULL  , color_hair varchar(255) NULL  , profession varchar(255) NULL  , homepage varchar(255) NULL  , newsletter tinyint(1) NULL DEFAULT 0  , check1 tinyint(1) NULL DEFAULT 0  , check2 tinyint(1) NULL DEFAULT 0  , check3 tinyint(1) NULL DEFAULT 0  , check4 tinyint(1) NULL DEFAULT 0  , amount_profile_viewed int(11) NULL DEFAULT 0  , amount_comments int(11) NULL DEFAULT 0  , amount_board_topics int(11) NULL DEFAULT 0  , amount_board_replies int(11) NULL DEFAULT 0  , amount_mail_inbox int(11) NULL DEFAULT 0  , amount_mail_outbox int(11) NULL DEFAULT 0  , showinfo tinyint(1) NULL DEFAULT 0  , first_login int(11) NULL DEFAULT 0  , last_login int(11) NULL DEFAULT 0  , last_action int(11) NULL DEFAULT 0  , stamp int(11) NULL DEFAULT 0  , status tinyint(4) NULL DEFAULT 1  , PRIMARY KEY(id))TYPE=MyISAM;
CREATE TABLE rex__user_comment ( id int(11) NOT NULL  auto_increment, user_id int(11) NULL DEFAULT 0  , from_user_id int(11) NULL DEFAULT 0  , message text NULL  , stamp varchar(14) NULL  , status tinyint(1) NULL DEFAULT 0  , PRIMARY KEY(id))TYPE=MyISAM;
CREATE TABLE rex__user_mail ( id int(11) NOT NULL  auto_increment, user_id int(11) NULL DEFAULT 0  , from_user_id int(11) NULL DEFAULT 0  , message text NULL  , stamp varchar(14) NULL  , status tinyint(1) NULL DEFAULT 0  , PRIMARY KEY(id))TYPE=MyISAM;
CREATE TABLE rex_action ( id int(11) NOT NULL  auto_increment, name varchar(255) NULL  , action text NULL  , prepost tinyint(4) NULL DEFAULT 0  , status tinyint(4) NULL DEFAULT 0  , PRIMARY KEY(id))TYPE=MyISAM;
CREATE TABLE rex_article ( id int(11) NOT NULL  auto_increment, name varchar(255) NULL  , linkname varchar(255) NULL  , beschreibung text NULL  , attribute text NULL  , file text NULL  , category_id int(11) NOT NULL DEFAULT 0  , type_id int(11) NULL DEFAULT 1  , startpage tinyint(1) NOT NULL DEFAULT 0  , prior int(10) NULL DEFAULT 0  , path varchar(255) NULL  , status tinyint(1) NULL DEFAULT 0  , online_von varchar(8) NULL DEFAULT 0  , online_bis varchar(8) NULL DEFAULT 0  , erstelldatum varchar(8) NULL DEFAULT 0  , suchbegriffe text NULL  , template_id int(5) NOT NULL DEFAULT 0  , checkbox01 tinyint(1) NULL DEFAULT 0  , checkbox02 tinyint(1) NULL DEFAULT 0  , checkbox03 tinyint(1) NULL DEFAULT 0  , checkbox04 tinyint(1) NULL DEFAULT 0  , user_login varchar(255) NULL  , PRIMARY KEY(id,category_id,startpage,template_id))TYPE=MyISAM;
CREATE TABLE rex_article_slice ( id int(11) NOT NULL  auto_increment, re_article_slice_id int(11) NOT NULL DEFAULT 0  , value1 text NULL  , value2 text NULL  , value3 text NULL  , value4 text NULL  , value5 text NULL  , value6 text NULL  , value7 text NULL  , value8 text NULL  , value9 text NULL  , value10 text NULL  , file1 varchar(255) NULL  , file2 varchar(255) NULL  , file3 varchar(255) NULL  , file4 varchar(255) NULL  , file5 varchar(255) NULL  , file6 varchar(255) NULL  , file7 varchar(255) NULL  , file8 varchar(255) NULL  , file9 varchar(255) NULL  , file10 varchar(255) NULL  , link1 int(11) NULL DEFAULT 0  , link2 int(11) NULL DEFAULT 0  , link3 int(11) NULL DEFAULT 0  , link4 int(11) NULL DEFAULT 0  , link5 int(11) NULL DEFAULT 0  , link6 int(11) NULL DEFAULT 0  , link7 int(11) NULL DEFAULT 0  , link8 int(11) NULL DEFAULT 0  , link9 int(11) NULL DEFAULT 0  , link10 int(11) NULL DEFAULT 0  , php text NULL  , html text NULL  , article_id int(11) NOT NULL DEFAULT 0  , modultyp_id int(5) NOT NULL DEFAULT 0  , PRIMARY KEY(id,re_article_slice_id,article_id,modultyp_id))TYPE=MyISAM;
CREATE TABLE rex_article_type ( type_id int(5) NOT NULL  auto_increment, name varchar(255) NULL  , description text NULL  , PRIMARY KEY(type_id))TYPE=MyISAM;
CREATE TABLE rex_category ( id int(11) NOT NULL  auto_increment, name varchar(255) NULL  , description text NULL  , func text NULL  , re_category_id int(11) NOT NULL DEFAULT 0  , prior int(10) NOT NULL DEFAULT 0  , path varchar(255) NOT NULL  , status tinyint(1) NOT NULL DEFAULT 0  , PRIMARY KEY(id,re_category_id,prior,path,status))TYPE=MyISAM;
CREATE TABLE rex_email ( email_id int(11) NOT NULL  auto_increment, sex char(1) NULL  , name varchar(255) NULL  , firstname varchar(255) NULL  , email varchar(255) NULL  , ustamp int(11) NULL DEFAULT 0  , PRIMARY KEY(email_id))TYPE=MyISAM;
CREATE TABLE rex_file ( file_id int(11) NOT NULL  auto_increment, re_file_id int(11) NULL DEFAULT 0  , category_id int(11) NULL DEFAULT 0  , filetype varchar(255) NULL  , filename varchar(255) NULL  , originalname varchar(255) NULL  , filesize varchar(255) NULL  , width int(11) NULL DEFAULT 0  , height int(11) NULL DEFAULT 0  , title varchar(255) NULL  , description text NULL  , copyright varchar(255) NULL  , stamp int(11) NULL DEFAULT 0  , user_login varchar(255) NULL  , PRIMARY KEY(file_id))TYPE=MyISAM;
CREATE TABLE rex_file_category ( id int(11) NOT NULL  auto_increment, name varchar(255) NULL  , re_id int(11) NULL DEFAULT 0  , path varchar(255) NULL  , PRIMARY KEY(id))TYPE=MyISAM;
CREATE TABLE rex_help ( id int(11) NOT NULL  auto_increment, page varchar(255) NULL  , name varchar(255) NULL  , description text NULL  , comment text NULL  , PRIMARY KEY(id))TYPE=MyISAM;
CREATE TABLE rex_module_action ( id int(11) NOT NULL  auto_increment, module_id int(11) NULL DEFAULT 0  , action_id int(11) NULL DEFAULT 0  , PRIMARY KEY(id))TYPE=MyISAM;
CREATE TABLE rex_modultyp ( id int(5) NOT NULL  auto_increment, label varchar(255) NULL  , name varchar(255) NULL  , category_id int(11) NOT NULL DEFAULT 0  , ausgabe text NULL  , bausgabe text NULL  , eingabe text NULL  , func text NULL  , php_enable tinyint(1) NOT NULL DEFAULT 0  , html_enable tinyint(1) NOT NULL DEFAULT 0  , PRIMARY KEY(id,category_id,php_enable,html_enable))TYPE=MyISAM;
CREATE TABLE rex_template ( id int(5) NOT NULL  auto_increment, label varchar(255) NULL  , name varchar(255) NULL  , content text NULL  , bcontent text NULL  , active tinyint(1) NULL DEFAULT 0  , date timestamp(8) NULL  , PRIMARY KEY(id))TYPE=MyISAM;
